                while(prey != yy || prex != xx){
                    bo[prey][prex] = '.';
                    prey -= dy[i];
                    prex -= dx[i];
                }
                dfs(nowy,nowx,dep+1);
                prey += dy[i];
                prex += dx[i];
                while(prey != nowy || prex != nowx){
                    bo[prey][prex] = 'X';
                    prey += dy[i];
                    prex += dx[i];
                }
                break;
            }
        }
    }
}

