typedef long long ll;

char F[20][16];
int sx, sy;
unordered_map<int, int> umap;

constexpr int to_num(int x, int y) {
  return x+y*16;
}

struct state {
  int x, y, cost, used;
  state(int x, int y, int cost, int used) : x(x), y(y), cost(cost), used(used) {}
  bool operator < (state const& s) const {
    return cost > s.cost;
  }
};

int const dx[8] = {-1, 0, 1, 0,-1, 1, 1,-1};
int const dy[8] = {0, -1, 0, 1,-1,-1, 1, 1};

int const INF = 1<<29;

bool inrange(int x, int y) {
  return 0<=x && x<16 && 0<=y && y<20;
}

bool is_used_xy(int used, int x, int y) {
  if(umap[to_num(x, y)] == -1) { return false; }
  return used >> umap[to_num(x, y)] & 1;
}

void debugger(int used, int cx, int cy) {
  rep(i, 20) {
    rep(j, 16) {
      if(cx == j && cy == i) {
        assert((F[i][j] == 'X' && is_used_xy(used, j, i)) || (F[i][j] == '.') || (F[i][j] == 'G'));
        cout << 'O';
      }
      else {
        cout << (is_used_xy(used, j, i) ? 'U' : F[i][j]);
      }
    }
    cout << endl;
  }
}

int dijkstra() {
  int dist[22][22];
  fill(dist[0], dist[0]+22*22, INF);
  priority_queue<state> pq;
  pq.emplace(sx, sy, 0, 0);
  dist[sy][sx] = 0;
  while(!pq.empty()) {
    const int x = pq.top().x, y = pq.top().y, cost = pq.top().cost, used = pq.top().used;
    pq.pop();
    if(x == -1 && y == 19) { return cost; }
    if(F[y][x] == 'G') { return cost; }
    rep(i, 8) {
      int nx = x+dx[i], ny = y+dy[i];
      int nused = used;
      if(!inrange(nx, ny)) { continue; }
      if(F[ny][nx] != 'X') { continue; }
      if(is_used_xy(used, nx, ny)) { continue; }
      bool ng = false;
      for(;;) {
        if(!inrange(nx, ny)) { ng = true; break; }
        if(F[ny][nx] != 'X') { break; }
        if(is_used_xy(used, nx, ny)) { break; }
        nused |= 1<<umap[to_num(nx, ny)];
        nx += dx[i], ny += dy[i];
      }
      if(ng && nx == -1 && ny == 19) { pq.emplace(-1,19,cost+1, nused); continue; }
      if(ng) { continue; }
      if(F[ny][nx] == 'A') { continue; }

      if(dist[ny][nx] <= cost+1) { continue; }
      dist[ny][nx] = cost+1;
      pq.emplace(nx, ny, dist[ny][nx], nused);
    }
  }
  return -1;
}

int main() {

  rep(i, 20) rep(j, 16) umap[to_num(j, i)] = -1;
  int bcnt = 0;
  rep(i, 19) rep(j, 15) {
    cin >> F[i][j];
    if(F[i][j] == 'O') {
      F[i][j] = '.';
      sx = j, sy = i;
    }
    if(F[i][j] == 'X') {
      umap[to_num(j,i)] = bcnt++;
    }
  }

  rep(i, 20) { F[i][15] = 'A'; }
  rep(i, 16) {
    if(i < 15 && F[18][i] != 'X') { F[18][i] = 'G'; }
    F[19][i] = 'G';
  }

  cout << dijkstra() << endl;

  return 0;
}

