							ret = min(ret, ret_s);
						}

						for (int k = 0; k < 19; ++k) {
							s[k] = tmp[k];
						}

						break;
					}

					nnx += vx[i], nny += vy[i];
				}
			}
		}
		else if (nnx == 19) {
			ret = min(ret, cnt);
		}
	}

	if (ret == (int)1e9) return -1;

	//cout << "return " << x << ", " << y << " = " << ret << endl;
	return ret;
