using namespace std;

typedef pair<int, int> P;
typedef pair<P, int> Q;

const int INF = 100000000;

char d[19][16];

int dx[8] = {0, 1, 1, 1, 0, -1, -1, -1};
int dy[8] = {-1, -1, 0, 1, 1, 1, 0, -1};

bool out(int x, int y) {
  return x < 0 || y < 0 || x >= 15 || y >= 19;
}

bool goal(int x, int y) {
  return y >= 19;
}

bool black(int x, int y) {
  return d[y][x] == 'X';
}

int solve(int X, int Y, int counter) {
  counter++;
  int ans = INF;
  for (int i = 0; i < 8; i++) {
    int x = X + dx[i], y = Y + dy[i];
    if (out(x, y)) continue;
    if (!black(x, y)) continue;
    while (!out(x, y) && black(x, y)) {
      d[y][x] = '.';
      x += dx[i], y += dy[i];
    }
    if (goal(x, y)) return counter;
    if (out(x, y)) continue;
    ans = min(ans, solve(x, y, counter));
    int j = (i + 4) % 8;
    x += dx[j], y += dy[j];
    while (!(x == X && y == Y)) {
      d[y][x] = 'X';
      x += dx[j], y += dy[j];
    }
  }
  return ans;
}

int main() {
  for (int i = 0; i < 19; i++) scanf("%s", d[i]);
  int x, y;
  for (int i = 0; i < 19; i++) {
    for (int j = 0; j < 15; j++) {
      if (d[i][j] == 'O') {
        d[i][j] = '.';
        x = j, y = i;
        i = 19,j = 16;
      }
    }
  }
  int ans = solve(x, y, 0);
  if (ans == INF) ans = -1;
  printf("%d\n", ans);
}

#include<bits/stdc++.h>
using namespace std;

struct state{
  int bi,a,b;
  bool operator<(const state& s) const {
    if( bi == s.bi ){
      if( a == s.a ) return b < s.b;
      return a < s.a;
    }
    return bi < s.bi;
  }
};

int dx[]={0,1,0,-1,1,1,-1,-1};
int dy[]={1,0,-1,0,1,-1,1,-1};


map<state,int> dp;
char cie[55][55];
int fie[55][55];
int N,W,H;
bool check( int bi,int x,int y,int d,int &nst,int &nx,int &ny ){
  bool f = false;
  nst = bi;
  nx = x + dx[d];
  ny = y + dy[d]; 
  if( nx < 0 || nx >= W || ny < 0 || ny >= H ) return false;
  if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
    f = true;     
    nst |= (1<<fie[nx][ny]);
  }

  while( f ){
    nx = nx + dx[d];
    ny = ny + dy[d]; 
    if( ny > H-1 || ( ny == H-1 && 0 <= nx && nx < W ) ) return true;
    if( nx < 0 || nx >= W || ny < 0 ) return false;
    if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
      nst |= (1<<fie[nx][ny]);
    } else
      break;
  }
  return f;
}

int solve(int bi,int x,int y){
  if( dp.find((state){bi,x,y}) != dp.end() ) return dp[(state){bi,x,y}];
  // cout << bitset<20>(bi) << " "<< x << " "<< y << endl;
  if( y > H-1 || ( y == H-1 && 0 <= x && x < W ) ) return dp[(state){bi,x,y}] = 0;
  int ret = (1<<29);
  for(int i=0;i<8;i++){
    int nx,ny,nst;
    if( check(bi,x,y,i,nst,nx,ny) ){
      ret = min( ret, solve(nst,nx,ny)+1);
    }
  }
  return dp[(state){bi,x,y}] = ret;
}

