  N = 0;
  W=15;H=19;
  int x,y;
  for(int i=0;i<19;i++){
    for(int j=0;j<15;j++){
      cin >> cie[j][i];
      if( cie[j][i] == 'X')
    fie[j][i] = N++;
      if( cie[j][i] == 'O' ){
    x = j; y = i;
      }
    }
  }
  int res = solve(0,x,y);
  if( res == (1<<29) )
    cout << -1 << endl;
  else
    cout << res << endl;
}

