    nx+=dx[dir];
  }

  if(T==0)s.cost=-2;
  s.y=ny;
  s.x=nx;
  s.cost++;
  s.S-=T;
  return s;
}

int solve(){

  queue< state > Q;
  Q.push((state){sy,sx,(1<<m)-1,0});

  while(!Q.empty()){
    state s=Q.front();Q.pop();
    int S=s.S;

    for(int i=0;i<m;i++)
      if(S>>i&1)
	t[py[i]][px[i]]=i;

    for(int i=0;i<8;i++){
      state next=calc(i,s);
      if(next.cost==-1)continue;
      if(next.y<1)continue;
      if(next.y>=19)return s.cost+1;
      if(next.x<1)continue;
      if(next.x>15)continue;
      Q.push(next);
    }
    for(int i=0;i<m;i++)
      if(S>>i&1)
	t[py[i]][px[i]]=-1;
  }
  return -1;
}

