using namespace std;

#define reps(i,f,n) for(int i=f;i<int(n);i++)
#define rep(i,n) reps(i,0,n)

const int N = 20, T = (1<<N), H = 19, W = 15;

set<int> memo[T];
int board[H][W];
int stx,sty;

class Q{
public:
	int x,y,cnt,state;
	Q(int x,int y,int cnt, int state):x(x),y(y),cnt(cnt),state(state){}
};

void input(){
	string a[H];

	rep(i,H)cin>>a[i];
	int cnt = 0;
	rep(i,H){
		rep(j,W){
			board[i][j] = -1;
			if(a[i][j] == 'X'){
				board[i][j] = cnt;
				cnt++;
			}
			if(a[i][j] == 'O'){
				stx = j; sty = i;
			}
		}
	}
}


int zahyo(int x,int y){
	return x + y * W;
}

bool hasmemo(Q u){
	return memo[u.state].find(zahyo(u.x,u.y)) != memo[u.state].end();
}

void setmemo(Q u){
	memo[u.state].insert(zahyo(u.x,u.y));
}

bool hasbit(int state, int i){
	return (state&(1<<i))>0;
}

bool isblack(int x,int y,int state){
	int n = board[y][x];
	if(n<0)return false;
	return !hasbit(state,n);
}

bool isout(int x,int y){
	if(y<0)return true;
	if(y<H){
		if(x<0 || x>=W)return true;
	}
	return false;
}

bool isgoal(int x,int y){
	if(y==H-1 && (x>=0 && x<=W-1))return true;
	if(y>=H) return true;
	return false;
}


int minblack(int x,int y,int state){
	int n = board[y][x];
	if(n<0)assert(0);
	if(hasbit(state,n))assert(0);

	return state + (1<<n);
}

int solve(){

	queue<Q> que;
	que.push(Q(stx,sty,0,0));

	while(!que.empty()){
		Q u = que.front(); que.pop();
		if(hasmemo(u))continue;
		setmemo(u);

		//printf("%d %d\n",u.x,u.y);

		int dx[] = {-1,0,1,-1,1,-1,0,1};
		int dy[] = {-1,-1,-1,0,0,1,1,1};

		rep(p,8){
			int st = u.state;
			reps(i,1,100){
				int nx = u.x + dx[p]*i;
				int ny = u.y + dy[p]*i;


				if(i==1){
					if(isout(nx,ny) || isgoal(nx,ny))break;
					if(!isblack(nx,ny,st))break;
				}
				if(i!=1){
					if(isout(nx,ny))break;
					if(isgoal(nx,ny))return u.cnt+1;
					if(!isblack(nx,ny,st)){
						que.push(Q(nx,ny,u.cnt+1,st));
						break;
					}
				}

				st = minblack(nx,ny,st);
			}
		}
	}
	return -1;
}

int main(){
	input();
	cout<<solve()<<endl;
}

