    int bit, y, x;
    tie(bit, y, x) = p;
    que.pop();
    for(int i = 0; i < 8; i++) {
      int ny = vy[i] + y, nx = vx[i] + x, bb = bit;
      while(nx >= 0 && nx < 15 && ny >= 0 && ny < 19 && S[ny][nx] < 20) {
        if((bit >> S[ny][nx]) & 1) break;
        bb |= 1 << S[ny][nx];
