#include<bits/stdc++.h>
#define INF (1e9)
#define N 19
#define M 15
using namespace std;
typedef pair<int,int> P;
typedef pair<int,P> P1;
int sy,sx,cnt;
map<P1,int> memo;
map<P,int> num;
string s[N];
int dy[8]={-1,-1,-1,0,0,1,1,1};
int dx[8]={-1,0,1,-1,1,-1,0,1};

int dfs(int A,int y,int x){
  if(memo.count(P1(A,P(y,x))))return memo[P1(A,P(y,x))];
  if(N-1<=y)return 1;
  if(y<0||x<0||N<=y||M<=x)return INF;
  cout<<endl;
  for(int i=0;i<N;i++){
    for(int j=0;j<M;j++){
      if(y==i&&x==j)cout<<'O';
      else cout<<s[y][x];
    }
    cout<<endl;
  }
  cout<<endl;
  int res=INF;
  for(int i=0;i<8;i++){
    int ny=y+dy[i],nx=x+dx[i],nA=A;
    if(ny<0||nx<0||N<=ny||M<=nx||s[ny][nx]!='X')continue;
    while(0<=ny&&ny<N&&0<=nx&&nx<M&&s[ny][nx]=='X'){
      nA|=(1<<num[P(ny,nx)]);
      s[ny][nx]='.';
      ny+=dy[i]; nx+=dx[i];
    }
    res=min(res,dfs(nA,ny,nx));
    while(ny!=y||nx!=x){
      ny-=dy[i]; nx-=dx[i];
      s[ny][nx]='X';
    }
    s[y][x]='.';
  }
  return memo[P1(A,P(y,x))]=res+1;
}

int main(){
  for(int i=0;i<N;i++){
    cin>>s[i];
    for(int j=0;j<M;j++){
      if(s[i][j]=='O'){
	sy=i,sx=j;
	s[i][j]='.';
      }
      if(s[i][j]=='X')
	num[P(i,j)]=cnt++;
    }
  }
  int ans=dfs(0,sy,sx);
  cout<<(ans>=INF?-1:ans-1)<<endl;
  return 0;
}

