  ios_base::sync_with_stdio(false);

  REP(i,19) cin >> bd[i];
  PII w;
  int gid = 0;
  REP(y,H) REP(x,W){
	if(bd[y][x] == 'O')
	  w = MP(y,x);
	else if(bd[y][x] == 'X')
	  id[y][x] = gid++;
  }

  int ans = -1;
  unordered_map<D,int> memo;
  queue<D> q;
  q.push(D(w,(1<<21)-1));
  memo[D(w,(1<<21)-1)] = 0;
  while(!q.empty()){
	D d = q.front();
	q.pop();
	PII p = d.FF;
	if(p.FF >= H-1){
	  ans = memo[d];
