constexpr double EPS = 1e-9;

//int N, M, K, H, W, L, R;
long long int N, M, K, H, W, L, R;

int Search(vector<string>&s, int y, int x) {
	int ret = MOD;
	for (int i = -1; i <= 1; i++) {
		for (int j = -1; j <= 1; j++) {
			if (i == 0 && j == 0)continue;
			bool flag = false;
			int ny = y + i, nx = x + j;
			while (ny >= 0 && ny < H&&nx >= 0 && nx < W&&s[ny][nx] == 'X') {
				flag = true;
				s[ny][nx] = '.';
				ny += i, nx += j;
			}
			if (flag&&ny >= 0 && ny < H&& nx >= 0 && nx < W) {
				ret = min(ret, Search(s, ny, nx));
			}
			if (flag&&(ny + 1 > H||(ny+1==H&&nx>=0&&nx<W))) {
				ret = 0;
			}
			ny -= i, nx -= j;
			while (ny != y || nx != x) {
				s[ny][nx] = 'X';
				ny -= i, nx -= j;
			}
		}
	}
	return ret + 1;
}

