    if(F[i][j] == 'O') {
      F[i][j] = '.';
      sx = j, sy = i;
    }
    if(F[i][j] == 'X') {
      umap[to_num(j,i)] = bcnt++;
    }
  }

  rep(i, 20) { F[i][15] = 'A'; }
  rep(i, 16) {
    if(i < 15 && F[18][i] != 'X') { F[18][i] = 'G'; }
    F[19][i] = 'G';
  }

  cout << dijkstra() << endl;

  return 0;
}

#include<bits/stdc++.h>

#define REP(i,s,n) for(int i=s;i<n;i++)
#define rep(i,n) REP(i,0,n)

using namespace std;

const int IINF = INT_MAX;
const int h = 19;
const int w = 15;

struct Data { int x,y,bitmask; };

char a[20][20];
int mindist[1<<20][20][20];
int Index[20][20];

bool isValid(int x,int y) { return 0 <= x && x < w && 0 <= y && y < h; }

void compute(){
  int black = 0;
  int sx=-1,sy=-1;
  rep(i,h) rep(j,w) {
    if( a[i][j] == 'X' ) Index[i][j] = black++;
    if( a[i][j] == 'O' ) sx = j, sy = i;
  }
  rep(i,(1<<black)) rep(j,h) rep(k,w) mindist[i][j][k] = IINF;
  mindist[(1<<black)-1][sy][sx] = 0;
  deque<Data> deq;
  deq.push_back((Data){sx,sy,(1<<black)-1});
  while( !deq.empty() ){
    Data data = deq.front(); deq.pop_front();
    REP(dx,-1,2){
      REP(dy,-1,2){
        if( dx == 0 && dy == 0 ) continue;
        int nx = data.x + dx, ny = data.y + dy, nbitmask = data.bitmask;
        bool valid = false;
        while( isValid(nx,ny) && ( a[ny][nx] == 'X' && ( data.bitmask & (1<<Index[ny][nx]) ) ) ){
          nbitmask &= ~(1<<Index[ny][nx]);
          nx += dx, ny += dy;
          valid = true;
        }
