using namespace std;

int H=19,W=15;

enum DIR{
	Left_Up,
	Up,
	Right_Up,
	Left,
	Right,
	Left_Down,
	Down,
	Right_Down,
};

DIR dir[8] = {Left_Up,Up,Right_Up,Left,Right,Left_Down,Down,Right_Down};

int diff_row[8] = {-1,-1,-1,0,0,1,1,1},diff_col[8] = {-1,0,1,-1,1,-1,0,1};

struct BlackStone{
	BlackStone(){
		row = col = 0;
	}

	BlackStone(short arg_row,short arg_col){
		row = arg_row;
		col = arg_col;
	}
	short row,col;
};

struct Info{
	vector<BlackStone> BLACK_STONE;
	bool operator<(const struct Info &arg) const{
			return row < arg.row;
	};
	short row,col,count;
};

bool rangeCheck(short row,short col){
	if(row <= 17){
		if(col < 0 || col > 14){
			return false;
		}else{
			return true;
		}
	}else{ //row >= 18
		if(row == 18 && (col < 0 || col > 14)){
			return false;
		}else{
			return true;
		}
	}
}

Info move(Info info,DIR dir){
	Info ret;

	char map[19][15];
	for(int i = 0; i < H; i++){
		for(int k = 0; k < W; k++)map[i][k] = '.';
	}

	for(int i = 0; i < info.BLACK_STONE.size(); i++){
		map[info.BLACK_STONE[i].row][info.BLACK_STONE[i].col] = 'X';
		BlackStone new_stone;
		new_stone.row = info.BLACK_STONE[i].row;
		new_stone.col = info.BLACK_STONE[i].col;
		ret.BLACK_STONE.push_back(new_stone);
	}
	ret.count = info.count+1;

	int next_row,next_col,index;

	switch(dir){
	case Left_Up:
		index = 0;
		break;
	case Up:
		index = 1;
		break;
	case Right_Up:
		index = 2;
		break;
	case Left:
		index = 3;
		break;
	case Right:
		index = 4;
		break;
	case Left_Down:
		index = 5;
		break;
	case Down:
		index = 6;
		break;
	case Right_Down:
		index = 7;
		break;
	}

	next_row = info.row+diff_row[index];
	next_col = info.col+diff_col[index];

	while(rangeCheck(next_row,next_col) == true && map[next_row][next_col] == 'X'){
		next_row += diff_row[index];
		next_col += diff_col[index];
	}


	if(next_row == info.row+diff_row[index] && next_col == info.col+diff_col[index]){
		ret.row = 1;
		ret.col = -1;
	}else{

		if(rangeCheck(next_row,next_col) == true && next_row <= 18){ //?????????????????????????????????????????????????

			int tmp_row,tmp_col;

			tmp_row = info.row+diff_row[index];
			tmp_col = info.col+diff_col[index];

			while(tmp_row != next_row || tmp_col != next_col){
				for(int i = 0; i < ret.BLACK_STONE.size();i++){
					if(ret.BLACK_STONE[i].row == tmp_row && ret.BLACK_STONE[i].col == tmp_col){
						ret.BLACK_STONE.erase(ret.BLACK_STONE.begin()+i);
						break;
					}
				}
				tmp_row += diff_row[index];
				tmp_col += diff_col[index];
			}
		}

		ret.row = next_row;
		ret.col = next_col;
	}

	return ret;
}

int main(){

	Info first;

	char buf[15];
	int minimum = BIG_NUM,start_row,start_col;

	for(short i = 0; i < H; i++){
		scanf("%s",buf);
		for(short k = 0; k < W; k++){
			if(buf[k] == 'O'){
				start_row = i;
				start_col = k;
			}else if(buf[k] == 'X'){
				first.BLACK_STONE.push_back(BlackStone(i,k));
			}
		}
	}

	first.row = start_row;
	first.col = start_col;
	first.count = 0;

	priority_queue<Info> Q;
	Q.push(first);

	while(!Q.empty()){

		if(Q.top().row >= 18){
			minimum = Q.top().count;
			break;
		}else{

			for(int i = 0; i < 8; i++){
				Info next = move(Q.top(),dir[i]);

				if(rangeCheck(next.row,next.col)){
					Q.push(next);
				}
			}

			Q.pop();
		}
	}

	if(minimum == BIG_NUM){
		printf("-1\n");
	}else{
		printf("%d\n",minimum);
	}

	return 0;
}

#include <bits/stdc++.h>

using namespace std;

namespace {

    const int INF = 1<<28;

    vector<string> F;
    void input() {
        F.resize(19);
        for (int i = 0; i < 19; i++) cin >> F[i];
    }

    int sy, sx;
    int c;
    vector<vector<int>> N;
    map<int,pair<int,int>> M;

    int memo[19][15][1<<20];

    void init() {
        c = 0;
        sy = sx = -1;
        N.clear(); N.resize(19, vector<int>(15, -1));
        M.clear();
        for (int i = 0; i < 19; i++) {
            for (int j = 0; j < 15; j++) {
                if (F[i][j] == 'O') {
                    sy = i;
                    sx = j;
                }
                if (F[i][j] == 'X') {
                    N[i][j] = c;
                    M[c] = make_pair(i, j);
                    c++;
                }
            }
        }

        memset(memo, -1, sizeof(memo));
    }

    struct NextPos {
        int y, x;
        int used;
        NextPos(int y, int x, int used) : y(y), x(x), used(used) {}
    };

    NextPos findNextPos(int y, int x, int dy, int dx, int used) {
        int nused = used;
        while (true) {
            int ny = y + dy;
            int nx = x + dx;
            if (ny < 0 || ny >= 19 || nx < 0 || nx >= 15) return NextPos(ny, nx, nused);
            if (F[ny][nx] == 'X') {
                int id = N[ny][nx];
                if (nused & (1 << id)) {
                    return NextPos(ny, nx, nused);
                } else {
                    nused |= (1 << id);
                }
            } else {
                return NextPos(ny, nx, nused);
            }
            y = ny;
            x = nx;
        }
    }

    bool movable(int y, int x, int dy, int dx, int used) {
        int ny = y + dy;
        int nx = x + dx;
        if (ny < 0 || ny >= 19 || nx < 0 || nx >= 15) return false;
        if (F[ny][nx] != 'X') return false;
        int id = N[ny][nx];
        if (used & (1 << id)) return false;
        return true;
    }

    int dfs(int y, int x, int used) {
        //cout << "dfs: " << y << " " << x << " " << used << endl;
        if (y >= 18) return 0;
        if (y < 0 || x < 0 || x >= 15) return INF;
        int& cache = memo[y][x][used];
        if (cache >= 0) return cache;
        int ans = INF;
        for (int dy = -1; dy <= 1; dy++) {
            for (int dx = -1; dx <= 1; dx++) {
                if (dy == 0 && dx == 0) continue;
                if (not movable(y, x, dy, dx, used)) continue;
                NextPos next = findNextPos(y, x, dy, dx, used);
                ans = min(ans, dfs(next.y, next.x, next.used) + 1);
            }
        }
        return cache = ans;
    }

    void solve() {
        init();
        int ans = dfs(sy, sx, 0);
        if (ans >= INF) ans = -1;
        cout << ans << endl;
    }
}

int main() {
    input();
    solve();
}

