using namespace std;

#define H 19
#define W 15
bool banmen[H+1][W];
int dirs[8][2];

int solve(int x, int y, int te) {
  //printf("%d,%d,%d\n",x,y,te);
  if(y>=H-1) return te;
  int ans = 9999;
  for(int dir_index = 0; dir_index < 8; dir_index++) {
    int dx = dirs[dir_index][0];
    int dy = dirs[dir_index][1];
    int count = 0;
    int ax = x+dx;
    int ay = y+dy;
    //printf("(%d,%d,%d,%d,%d)\n",ax>0,ax+dx<W,ay>0,ay+dy<=H,banmen[ax][ay]);
    while(ax>0&&ax+dx<W&&ay>0&&ay+dy<=H && banmen[ay][ax]) {
      banmen[ay][ax] = false;
      ax+=dx;
      ay+=dy;
      count++;
    }
    //printf("(x,y,c)=(%d,%d,%d)\n",dx,dy,count);
    if(count>0) {
      int ret = solve(ax,ay,te+1);
      if(ret!=-1) {
        ans = min(ans,ret);
      }
    }
    for(int i = 1; i <= count; i++) {
      banmen[y+dy*i][x+dx*i] = true;
    }
  }
  return (ans==9999)?-1:ans;
}

