using namespace std;

const int vy[] = {-1, -1, -1, 0, 1, 1, 1, 0};
const int vx[] = {-1, 0, 1, 1, 1, 0, -1, -1};

string S[19];
int sx, sy;

bool isgoal(int y, int x)
{
  return ((y == 18 && 0 <= x && x <= 14) || y == 19);
}

int bfs()
{
  unordered_map< int, int > v[19][15];
  queue< tuple< int, int, int > > que;
  que.emplace(0, sy, sx);
  v[sy][sx][0] = 0;
  while(!que.empty()) {
    int bit, y, x;
    tie(bit, y, x) = que.front();
    que.pop();
    for(int i = 0; i < 8; i++) {
      int ny = vy[i] + y, nx = vx[i] + x, bb = bit;
      while(nx >= 0 && nx < 15 && ny >= 0 && ny < 19 && S[ny][nx] < 20) {
        if((bit >> S[ny][nx]) & 1) break;
        bb |= 1 << S[ny][nx];
        ny += vy[i], nx += vx[i];
      }
      if(ny == vy[i] + y && nx == vx[i] + x) continue;
      if(isgoal(ny, nx)) return (v[y][x][bit] + 1);
      if(ny >= 0 && nx < 15 && ny >= 0 && ny < 19) {
        auto nxt = make_tuple(bb, ny, nx);
        if(v[ny][nx].count(bb) && v[ny][nx][bb] <= v[y][x][bit] + 1)continue;
        v[ny][nx][bb] = v[y][x][bit] + 1;
        que.emplace(nxt);
      }
    }
  }
  return (-1);
}

