#include <bits/stdc++.h>
using namespace std;

const int H = 19;
const int W = 15;
vector<string> board(H);
vector<int> xhs, xws;
int d[1<<20][H][W];

class State{
public:
    int xstate;
    int h, w;

//    bool operator < (const State &r) const{
//        int xl = xstate * H * W + h * W + w;
//        int xr = r.xstate * H * W + r.h * W + r.w;
//        return xl < xr;
//    }
};

inline bool infield(int h, int w){
    if(h == H)
        return true;
    else
        return 0<=w && w<W;
}

int bfs(int sh, int sw){
    int xn = xhs.size();
    map<pair<int, int>, int> ipoint;
    for(int i=0; i<xn; i++)
        ipoint[{xhs[i], xws[i]}] = i;

    queue<State> que;
    State s;
    s.xstate = (1<<xn)-1;
    s.h = sh;
    s.w = sw;
    que.push(s);
    d[s.xstate][s.h][s.w] = 0;

    while(!que.empty()){
        auto s = que.front(); que.pop();

        for(int dh=-1; dh<=1; dh++){
            for(int dw=-1; dw<=1; dw++) if(dh != 0 || dw != 0){
                State ns;
                ns.xstate = s.xstate;
                ns.h = s.h + dh;
                ns.w = s.w + dw;

                if(ipoint.find({ns.h, ns.w}) == ipoint.end() || ((ns.xstate>>ipoint[{ns.h, ns.w}])&1) == 0)
                    continue;

                int count = 0;
                while(infield(ns.h, ns.w) && ipoint.find({ns.h, ns.w})!= ipoint.end() && (ns.xstate>>ipoint[{ns.h, ns.w}])&1){
                    ns.h += dh;
                    ns.w += dw;
                    count++;
                }
                if(!infield(ns.h, ns.w))
                    continue;

                ns.h = s.h + dh;
                ns.w = s.w + dw;
                for(int i=0; i<count; i++){
                    ns.xstate -= 1<<ipoint[{ns.h, ns.w}];
                    ns.h += dh;
                    ns.w += dw;
                }

                que.push(ns);
                d[ns.xstate][ns.h][ns.w] = d[s.xstate][s.h][s.w] + 1;

                if(H-1<=ns.h){
                    return d[ns.xstate][ns.h][ns.w];
                }
            }
        }
    }
    return -1;
}

int main(){
    for(auto &s: board) cin >> s;

    int sh, sw;
    for(int h=0; h<H; h++){
        for(int w=0; w<W; w++){
            if(board[h][w] == 'O')
                sh = h, sw = w;
            if(board[h][w] == 'X')
                xhs.push_back(h), xws.push_back(w);
        }
    }
    cout << bfs(sh, sw) << endl;

    return 0;
}

#include <stdio.h>
#include <cmath>
#include <algorithm>
#include <cfloat>
#include <stack>
#include <queue>
#include <vector>
#include <string.h>
typedef long long int ll;
#define BIG_NUM 2000000000
#define MOD 1000000007
#define EPS 0.000001
using namespace std;

int H=19,W=15;

enum DIR{
	Left_Up,
	Up,
	Right_Up,
	Left,
	Right,
	Left_Down,
	Down,
	Right_Down,
};

DIR dir[8] = {Left_Up,Up,Right_Up,Left,Right,Left_Down,Down,Right_Down};

int diff_row[8] = {-1,-1,-1,0,0,1,1,1},diff_col[8] = {-1,0,1,-1,1,-1,0,1};

struct BlackStone{
	void set(short arg_row,short arg_col){
		row = arg_row;
		col = arg_col;
	}
	short row,col;
	bool erased;
};

struct Info{
	BlackStone black_stone[20];
	short row,col,black_num;
	int count;
};

bool rangeCheck(int row,int col){
	if(row <= 17){
		if(col < 0 || col > 14){
			return false;
		}else{
			return true;
		}
	}else{ //row >= 18
		if(row == 18 && (col < 0 || col > 14)){
			return false;
		}else{
			return true;
		}
	}
}

Info move(Info info,DIR dir){
	Info ret;

	char map[19][15];
	for(int i = 0; i < H; i++){
		for(int k = 0; k < W; k++)map[i][k] = '.';
	}

	for(int i = 0; i < info.black_num; i++){
		if(info.black_stone[i].erased == false){
			map[info.black_stone[i].row][info.black_stone[i].col] = 'X';
		}
		ret.black_stone[i].set(info.black_stone[i].row,info.black_stone[i].col);
		ret.black_stone[i].erased = info.black_stone[i].erased;
	}
	ret.black_num = info.black_num;
	ret.count = info.count+1;

	int next_row,next_col,index;

	switch(dir){
	case Left_Up:
		index = 0;
		break;
	case Up:
		index = 1;
		break;
	case Right_Up:
		index = 2;
		break;
	case Left:
		index = 3;
		break;
	case Right:
		index = 4;
		break;
	case Left_Down:
		index = 5;
		break;
	case Down:
		index = 6;
		break;
	case Right_Down:
		index = 7;
		break;
	}

	next_row = info.row+diff_row[index];
	next_col = info.col+diff_col[index];

	while(rangeCheck(next_row,next_col) == true && map[next_row][next_col] == 'X'){
		next_row += diff_row[index];
		next_col += diff_col[index];
	}


	if(next_row == info.row+diff_row[index] && next_col == info.col+diff_col[index]){
		ret.row = 1;
		ret.col = -1;
	}else{
		int tmp_row,tmp_col;

		tmp_row = info.row+diff_row[index];
		tmp_col = info.col+diff_col[index];

		while(tmp_row != next_row || tmp_col != next_col){
			for(int i = 0; i < ret.black_num;i++){
				if(ret.black_stone[i].erased == false && ret.black_stone[i].row == tmp_row && ret.black_stone[i].col == tmp_col){
					ret.black_stone[i].erased = true;
					break;
				}
			}
			tmp_row += diff_row[index];
			tmp_col += diff_col[index];
		}

		ret.row = next_row;
		ret.col = next_col;
	}

	return ret;
}

int main(){

	Info first;
	first.black_num = 0;
	char buf[15];
	int minimum = BIG_NUM,start_row,start_col;

	for(int i = 0; i < H; i++){
		scanf("%s",buf);
		for(int k = 0; k < W; k++){
			if(buf[k] == 'O'){
				start_row = i;
				start_col = k;
			}else if(buf[k] == 'X'){
				first.black_stone[first.black_num++].set(i,k);
			}
		}
	}

	first.row = start_row;
	first.col = start_col;
	first.count = 0;

	queue<Info> Q;
	Q.push(first);

	while(!Q.empty()){

		if(Q.front().row >= 18){
			minimum = Q.front().count;
			break;
		}else{

			for(int i = 0; i < 8; i++){
				Info next = move(Q.front(),dir[i]);

				if(rangeCheck(next.row,next.col)){
					Q.push(next);
				}
			}

			Q.pop();
		}
	}

	if(minimum == BIG_NUM){
		printf("-1\n");
	}else{
		printf("%d\n",minimum);
	}

	return 0;
}

#include "bits/stdc++.h"
using namespace std;
// http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=2612&lang=jp
#define INF 1<<30
#define H 19
#define W 15
typedef long long ll;
typedef pair<int, int> pii;
int ans = INF;
int dx[8] = { 1,1,0,-1,-1,-1,0,1 };
int dy[8] = { 0,-1,-1,-1,0,1,1,1 };

bool check(int x, int y, vector<vector<char>>& masu,int& dir) {
	int nx = x + dx[dir], ny = y + dy[dir];
	if (masu[nx][ny] != 'X')return false;
	while (true) {
		nx += dx[dir]; ny += dy[dir];
		if (masu[nx][ny] == 'X')continue;
		if (masu[nx][ny] == '#')return false;
		if (masu[nx][ny] == '.')return true;
	}
	return false;
}

void erase_move(int&x, int&y, vector<vector<char>>& masu, int& dir) {
	while (true) {
		x += dx[dir]; y += dy[dir];
		if (masu[x][y] == 'X') {
			masu[x][y] = '.';
			continue;
		}
		if (masu[x][y] == '.')return ;
	}
}

void dfs(int x, int y,int cnt,vector<vector<char>>& masu) {
	if (cnt + 1 >= ans) return;
	for (int dir = 0; dir < 8; dir++) {
		if (!check(x, y, masu, dir))continue;
		auto new_masu = masu;
		int new_x = x, new_y = y;
		erase_move(new_x,new_y, new_masu, dir);

		if (new_x >= 19) {
			ans = min(ans, cnt + 1);
			return;
		}

		dfs(new_x, new_y, cnt + 1, new_masu);
	}
}

int main() {
	cin.tie(0); ios::sync_with_stdio(false);
	vector<vector<char>> masu(H + 2, vector<char>(W + 2,'#'));
	int sx, sy;
	/* input */
	masu[H + 1][0] = masu[H + 1][W + 1] = '.';
	for (int i = 1; i <= H+1; i++){
		for (int j = 1; j <= W; j++){
			if (i == H + 1) masu[i][j] = '.';
			else {
				cin >> masu[i][j];
				if (masu[i][j] == 'O') { sx = i; sy = j; masu[i][j] = '.'; }
			}
		}
	}
	dfs(sx, sy, 0, masu);
	cout << (ans == INF ? -1 : ans) << endl;
	return 0;
}

