using namespace std;

typedef pair<int, int> P;
typedef pair<P, int> Q;

const int INF = 100000000;

char d[19][16];

int dx[8] = {0, 1, 1, 1, 0, -1, -1, -1};
int dy[8] = {-1, -1, 0, 1, 1, 1, 0, -1};

bool out(int x, int y) {
  return x < 0 || y < 0 || x >= 15 || y >= 19;
}

bool goal(int x, int y) {
  return y >= 19;
}

bool black(int x, int y) {
  return d[y][x] == 'X';
}

void ret(int x, int y, int X, int Y, int j) {
  x += dx[j], y += dy[j];
  while (!(x == X && y == Y)) {
    d[y][x] = 'X';
    x += dx[j], y += dy[j];
  }
}

int solve(int X, int Y, int counter) {
  counter++;
  int ans = INF;
  for (int i = 0; i < 8; i++) {
    int x = X + dx[i], y = Y + dy[i];
    if (out(x, y)) continue;
    if (!black(x, y)) continue;
    while (!out(x, y) && black(x, y)) {
      d[y][x] = '.';
      x += dx[i], y += dy[i];
    }
    if (goal(x, y)) {
      ret(x, y, X, Y, (i + 4) % 8);
      return counter;
    }
    if (out(x, y)) {
      ret(x, y, X, Y, (i + 4) % 8);
      continue;
    }
    ans = min(ans, solve(x, y, counter));
    ret(x, y, X, Y, (i + 4) % 8);
  }
  return ans;
}

int main() {
  for (int i = 0; i < 19; i++) scanf("%s", d[i]);
  int x, y;
  for (int i = 0; i < 19; i++) {
    for (int j = 0; j < 15; j++) {
      if (d[i][j] == 'O') {
        d[i][j] = '.';
        x = j, y = i;
        i = 19,j = 16;
      }
    }
  }
  int ans = solve(x, y, 0);
  if (ans == INF) ans = -1;
  printf("%d\n", ans);
}

