#define P(T1,T2) pair<T1,T2>
#define MP(a,b) make_pair((a),(b))
#define NUM_Y 19
#define NUM_X 15

char MAP[NUM_Y][NUM_X] = {};
int TMP_X[8] = { 1,1,1,0,0,-1,-1,-1 };
int TMP_Y[8] = { -1,0,1,-1,1,-1,0,1 };
int goal_cnt = 99999;

void show()
{
	REP(i, NUM_Y)
	{
		REP(j, NUM_X)
		{
			cout << MAP[i][j];
		}
		cout << endl;
	}
}

void BFS(int _sy, int _sx)
{

	struct Data
	{
		Data(int _x, int _y, int _c, bool _j) :x(_x), y(_y), c(_c), j(_j) {};
		int x = 0, y = 0;
		int c = 0;
		bool j = false;
	};

	stack<Data> que;
	que.push(Data(_sx, _sy, 0, false));

	while (que.empty() == false)
	{
		int y = que.top().y;
		int x = que.top().x;
		int c = que.top().c;
		bool j = que.top().j;
		que.pop();

		REP(i, 8)
		{
			int xx = x + TMP_X[i];
			int yy = y + TMP_Y[i];

			if (yy < 0 || yy >= NUM_Y || xx < 0 || xx >= NUM_X)
			{
				continue;
			}

			else if (MAP[yy][xx] == '.')
			{
				continue;
			}

			else if (MAP[yy][xx] == 'X')
			{
				MAP[y][x] = '.';
				while (true)
				{
					yy += TMP_Y[i];
					xx += TMP_X[i];
					if (yy >= NUM_Y)
					{
						if (goal_cnt > c+1)
						{
							goal_cnt = c+1;
						}
						break;
					}
					else if (yy < 0 || xx < 0 || xx >= NUM_X)
					{
						break;
					}
					else if (MAP[yy][xx] == 'X')
					{
						MAP[yy - TMP_Y[i]][xx - TMP_X[i]] = '.';
					}
					else if(MAP[yy][xx]=='.')
					{
						MAP[yy - TMP_Y[i]][xx - TMP_X[i]] = '.';
						MAP[yy][xx] = 'O';
						que.push(Data(xx, yy, c+1, false));
						i = 999;
						break;
					}
				}
			}
		}
		show();
	}
}

int main()
{
	int sx, sy;
	REP(y, 19)
	{
		REP(x, 15)
		{
			cin >> MAP[y][x];
			if (MAP[y][x] == 'O')
			{
				sy = y;
				sx = x;
			}
		}
	}

	BFS(sy, sx);

	if (goal_cnt == 99999)cout << -1 << endl;
	else cout << goal_cnt << endl;

	return 0;
}

