using namespace std;

const char h = 20, w = 17;
vector<vector<bool>> c;
typedef tuple<pair<char, char>, vector<vector<bool>>, char> T;
queue<T> q;
map<vector<vector<bool>>, vector<vector<char>>> memo;

const int dy[16] = { 0,-1, 0, 1, 1,-1, 1,-1, 0,-2, 0, 2};
const int dx[16] = { 1, 0,-1, 0, 1, 1,-1,-1, 2, 0,-2, 0};

int main(){
	c = vector<vector<bool>>(h, vector<bool>(w,0));

	pair<char, char> s;
	rep(i,h - 1){
		rep(j,w - 2){
			char a;
			cin >> a;
			if(a == 'X') c[i][j + 1] = 1;
			else c[i][j + 1] = 0;
			if(a == 'O'){
				s = make_pair(i,j + 1);
			}
		}
	}

	q.push(T(s,c,0));

	char ans = 127;
	while(not q.empty()){
		char cost;
		tie(s,c,cost) = q.front(); q.pop();

		char y, x;
		tie(y,x) = s;

		//rep(i,h){ rep(j,w){ if(i == y and j == x) cout << 'O'; else cout << c[i][j]; } cout << endl; } cout << endl;

		if(y == 18 or y == 19){
			ans = min(ans, cost);
			continue;
		}

		if(cost >= 20) continue;

		rep(i,8){
			char ny = y + dy[i];
			char nx = x + dx[i];
			if(ny < 0 || ny >= h || nx < 0 || nx >= w) continue;
			if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)) continue;
			vector<vector<bool>> pc = c;
			if(pc[ny][nx] == 1){
				while(true){
					if(ny < 0 || ny >= h || nx < 0 || nx >= w) {
						ny = -1;
						break;
					}
					if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)){
						ny = -1;
						break;
					}
					if(pc[ny][nx] == 0) break;
					pc[ny][nx] = 0;
					ny = ny + dy[i];
					nx = nx + dx[i];
				}
				if(ny == -1) continue;

				if(memo.count(pc)){
					auto& it = memo[pc];
					if(it[ny][nx] > cost + 1){
						it[ny][nx] = cost + 1;
						q.push(T(make_pair(ny,nx), pc, cost + 1));
					}
				}else{
					vector<vector<char>> dis(h, vector<char>(w,127));
					dis[ny][nx] = cost + 1;
					memo[pc] = dis;
					q.push(T(make_pair(ny,nx), pc, cost + 1));
				}
			}
		}
	}
	cout << (ans == 127 ? -1 : ans) << endl;
}

#include <bits/stdc++.h>
using namespace std;

const int H = 19;
const int W = 15;
vector<string> board(H);
vector<int> xhs, xws;
int d[1<<20][H][W];

struct State{
    int xstate;
    unsigned char h, w;

//    bool operator < (const State &r) const{
//        int xl = xstate * H * W + h * W + w;
//        int xr = r.xstate * H * W + r.h * W + r.w;
//        return xl < xr;
//    }
};

inline bool infield(int h, int w){
    if(h == H)
        return true;
    else
        return 0<=w && w<W;
}

int bfs(int sh, int sw){
    int xn = xhs.size();
    map<pair<int, int>, int> ipoint;
    for(int i=0; i<xn; i++)
        ipoint[{xhs[i], xws[i]}] = i;

    queue<State> que;
    State s;
    s.xstate = (1<<xn)-1;
    s.h = sh;
    s.w = sw;
    que.push(s);

    memset(d, -1, sizeof(d));
    d[s.xstate][s.h][s.w] = 0;

    while(!que.empty()){
        auto s = que.front(); que.pop();

        for(int dh=-1; dh<=1; dh++){
            for(int dw=-1; dw<=1; dw++) if(dh != 0 || dw != 0){
                State ns;
                ns.xstate = s.xstate;
                ns.h = s.h + dh;
                ns.w = s.w + dw;

                if(ipoint.find({ns.h, ns.w}) == ipoint.end() || ((ns.xstate>>ipoint[{ns.h, ns.w}])&1) == 0)
                    continue;

                int count = 0;
                while(infield(ns.h, ns.w) && ipoint.find({ns.h, ns.w})!= ipoint.end() && (ns.xstate>>ipoint[{ns.h, ns.w}])&1){
                    ns.h += dh;
                    ns.w += dw;
                    count++;
                }
                if(!infield(ns.h, ns.w))
                    continue;

                ns.h = s.h + dh;
                ns.w = s.w + dw;
                for(int i=0; i<count; i++){
                    ns.xstate -= 1<<ipoint[{ns.h, ns.w}];
                    ns.h += dh;
                    ns.w += dw;
                }

                if(d[ns.xstate][ns.h][ns.w] >= 0)
                    continue;

                que.push(ns);
                d[ns.xstate][ns.h][ns.w] = d[s.xstate][s.h][s.w] + 1;

                if(H-1<=ns.h){
                    return d[ns.xstate][ns.h][ns.w];
                }
            }
        }
    }
    return -1;
}

int main(){
    for(auto &s: board) cin >> s;

    int sh, sw;
    for(int h=0; h<H; h++){
        for(int w=0; w<W; w++){
            if(board[h][w] == 'O')
                sh = h, sw = w;
            if(board[h][w] == 'X')
                xhs.push_back(h), xws.push_back(w);
        }
    }
    cout << bfs(sh, sw) << endl;

    return 0;
}

#include <stdio.h>
