//const int dx[] = { -1, 0, 0, 1 }, dy[] = { 0, -1, 1, 0 };
const int dx[] = { -1, -1, -1, 0, 0, 1, 1, 1 }, dy[] = { 0, -1, 1, -1, 1, 0, -1, 1 };
bool valid(int x, int y, int h, int w) { return (x >= 0 && y >= 0 && x < h&&y < w); }
int place(int x, int y, int w) { return w*x + y; }

const int h = 19, w = 15;

int main()
{
	cin.sync_with_stdio(false);
	vs fld(h);
	REP(i, h) cin >> fld[i];
	vector<vi> id(h, vi(w, -1));
	vector<pii> place;
	int cnt = 0;
	pii init;
	REP(i, h)REP(j, w)
	{
		if (fld[i][j] == '.') continue;
		if (fld[i][j] == 'O')
		{
			init = pii(i, j);
			fld[i][j] = '.';
		}
		else
		{
			id[i][j] = cnt;
			place.push_back(pii(i, j));
			cnt++;
		}
	}
	map<pair<int, pii>, int> mp;
	queue<pair<int, pii>> que;
	que.emplace(0, init);
	while (!que.empty())
	{
		auto tmp = que.front(); que.pop();
		int bit = tmp.first, tx = tmp.second.first, ty = tmp.second.second;
		//cout << bitset<20>(bit) << " " << tx << " " << ty << endl;
		if (tx >= h - 1)
		{
			cout << mp[tmp] << endl;
			return 0;
		}
		REP(i, 8)
		{
			int nx = tx + dx[i], ny = ty + dy[i];
			if (!valid(nx, ny, h, w) || fld[nx][ny] == '.' || (bit >> id[nx][ny]) & 1) continue;
			vi remove;
			while (valid(nx, ny, h, w) && fld[nx][ny] != '.' && ((bit >> id[nx][ny]) & 1) == 0)
			{
				remove.push_back(id[nx][ny]);
				nx += dx[i], ny += dy[i];
			}
			if (!valid(nx,ny,h,w) && nx != h) continue;
			int nbit = bit;
			for (auto j : remove) nbit |= 1 << j;
			pair<int, pii> nex(nbit, pii(nx, ny));
			if (mp.count(nex)) continue;
			mp[nex] = mp[tmp] + 1;
			que.push(nex);
		}
	}
	cout << -1 << endl;
	return 0;
}

