const int mod=1000000007,MAX=55,INF=1<<30;
int ans=-1;
int H=20,W=17;

vector<int> dh={-1,0,1,1,1,0,-1,-1},dw={1,1,1,0,-1,-1,-1,0};

set<vector<pair<int,int>>> SE;

void BFS(vector<pair<int,int>> &s){
    queue<pair<vector<pair<int,int>>,int>> Q;
    Q.push(mp(s,0));

    while(!Q.empty()){
        auto a=Q.front();Q.pop();

        if(SE.count(a.fi)) continue;
        SE.insert(a.fi);

        vector<string> S(H,string(W,'.'));

        int sh,sw;

        for(auto x:a.fi){
            if(x.fi>=100){
                S[x.fi-100][x.se-100]='O';
                sh=x.fi-100;
                sw=x.se-100;
            }
            else S[x.fi][x.se]='X';
        }

        for(int j=0;j<W;j++){
            if(S[H-1][j]=='O'){
                ans=a.se;
                while(!Q.empty()) Q.pop();
            }
            if(j>=1&&j<=15&&S[H-2][j]=='O'){
                ans=a.se;
                while(!Q.empty()) Q.pop();
            }
        }
        if(ans!=-1) break;

        for(int i=0;i<H;i++){
            for(int j=0;j<W;j++){
                if(S[i][j]=='O'){
                    sh=i;
                    sw=j;
                }
            }
        }

        if(sw==0||sw==W-1) continue;

        for(int k=0;k<8;k++){
            int toh=sh+dh[k],tow=sw+dw[k];
            while(toh>=0&&toh<H&&tow>=0&&tow<W&&S[toh][tow]=='X'){
                toh+=dh[k];
                tow+=dw[k];
            }
            if(toh==sh+dh[k]&&tow==sw+dw[k]) continue;

            vector<string> T=S;
            T[sh][sw]='.';
            toh=sh+dh[k];tow=sw+dw[k];
            while(toh>=0&&toh<H&&tow>=0&&tow<W&&S[toh][tow]=='X'){
                T[toh][tow]='.';
                toh+=dh[k];
                tow+=dw[k];
            }
            T[toh][tow]='O';

            vector<pair<int,int>> to;

            for(int i=0;i<H;i++){
                for(int j=0;j<W;j++){
                    char c=T[i][j];
                    if(c=='O') to.push_back(mp(100+i,100+j));
                    if(c=='X') to.push_back(mp(i,j));
                }
            }

            Q.push(mp(to,a.se+1));
        }
    }
}

