{
	pair<int, int> start;
	vector<vector<bool>> goban;
	REP(i, H) {
		string tmp;
		vector<bool> row;
		cin >> tmp;
		REP(j,W){
			char c = tmp[j];
			row.push_back(c == 'X');
			if (c == 'O')start = { i,j };
		}
		goban.push_back(row);
	}

	step(goban, start, 1);
	if (ans == INF)ans = -1;
	cout << ans << endl;

	debug();
	return 0;
}

