int solve(State p) {
	bool OK3 = false;
	for (int i = 0; i < 15; i++) { if (p.x[18][i] == 1 || p.x[17][i] == 1)OK3 = true; }
	if (OK3 == false)return 999999;
	int cx = 0, cy = 0;
	for (int i = 0; i < 285; i++) {
		if (p.x[i / 15][i % 15] == 2) { cx = i / 15; cy = i % 15; }
	}
	vector<int>vec;
	for (int i = 0; i < 8; i++) {
		int ex = cx + dx[i], ey = cy + dy[i];
		if (ex < 0 || ey < 0 || ex >= 19 || ey >= 15)continue;
		if (p.x[ex][ey] == 0)continue;
		vec.push_back(i);
	}
	if (vec.size() == 0)return 999999;
	int minx = 999999;
	for (int i = 0; i < 8; i++) {
		bool OK2 = false; for (int j = 0; j < vec.size(); j++) { if (vec[j] == i)OK2 = true; }
		if (OK2 == false)continue;
		State T = p;
		int ex = cx, ey = cy;
