const ll INF = 1e18;

int h = 19, w = 15;
vector<map<pii, int> > dp;
vi dx = {0, 1, 1, 1, 0, -1, -1, -1}, dy = {-1, -1, 0, 1, 1, 1, 0, -1};

bool in(int x, int a, int b) {
	return a <= x and x < b;
}

int main() {
	int n = 0;
	vii a(h, vi(w, -1));
	pii start;
	rep (i, h) {
		rep (j, w) {
			char c;
			cin >> c;
			if (c == 'O') {
				start = pii(i, j);
			}
			if (c == 'X') {
				a[i][j] = n;
				n++;
			}
		}
	}
	int m = 1 << n;
	dp.resize(m);
	dp[m - 1][start] = 0;

	int ans = 1e9;
	for (int i = m - 1; i >= 0; i--) {
		for (auto itr = dp[i].begin(); itr != dp[i].end(); itr++) {
			int y = itr->first.first, x = itr->first.second;
			int cost = itr->second;
			rep (k, 8) {
				int ny = y + dy[k], nx = x + dx[k];
				vi use;
				bool is_black = false;
				while (in(ny, 0, h) and in(nx, 0, w)) {
					if (a[ny][nx] == -1) break;
					if ((i & (1 << a[ny][nx])) == 0) break;
					is_black = true;
					use.push_back(a[ny][nx]);
					ny += dy[k];
					nx += dx[k];
				}
				if (not is_black) continue;
				if (ny == h or (ny == h - 1 and in(nx, 0, w))) {
					ans = min(ans, cost + 1);
				}
				else if (ny == -1 or not in(nx, 0, w)) {
					continue;
				}
				else {
					int ni = i;
					rep (k, use.size()) {
						ni -= (1 << use[k]);
					}
					if (dp[ni][pii(ny, nx)] == 0) {
						dp[ni][pii(ny, nx)] = cost + 1;
					}
					else {
						dp[ni][pii(ny, nx)] = min(dp[ni][pii(ny, nx)], cost + 1);
					}
				}
			}
		}
	}
	if (ans >= 1e8) {
		cout << -1 << endl;
	}
	else{
		cout << ans << endl;
	}
}

