    REP(j,15) {
      if (t[i][j] == 'X') {
        idx[i][j] = vb.size();
        vb.emplace_back(i,j);
      } else if (t[i][j] == 'O') {
        si = i;
        sj = j;
      }
    }
  }
  int n = vb.size();
  vector<vector<int>> dp(1<<n, vector<int>(15*19+1, INF));
  dp[0][si*15+sj] = 0;
  REP(i,1<<n) {
    REP(j,15*19) {
      if (dp[i][j] == INF) continue;
      int oi = j/15;
      int oj = j%15;
      int di[8] = {1, 1, 1, 0, -1, -1, -1, 0};
      int dj[8] = {-1, 0, 1, 1, 1, 0, -1, -1};
      REP(k,8) {
        int l = 1;
        vector<int> ei;
        int ni;
        int nj;
        while (1) {
          ni = oi + l*di[k];
          nj = oj + l*dj[k];
          if (ni < 0 || ni >= 19 || nj < 0 || nj >= 15) break;
          if (idx[ni][nj] < 0 || ((i>>idx[ni][nj])&1)) break;
          ei.push_back(idx[ni][nj]);
          ++l;
