            dp[pi][15*19] = min(dp[pi][15*19], dp[i][j]+1);
          } else if (ni >= 0 && nj >= 0 && nj < 15) {
            dp[pi][ni*15+nj] = min(dp[pi][ni*15+nj], dp[i][j]+1);
          }
        }
      }
    }
  }
  int res = INF;
  REP(i,1<<n) res = min(res, dp[i][15*19]);
  if (res < INF) {
    cout << res << endl;
  } else {
    cout << -1 << endl;
  }
  return 0;
}

