typedef tuple<int,int,int,int> state;


string board[19];
int stone[19][15];

int num=0;

int dx[8]={1,1,1,0,-1,-1,-1,0};
int dy[8]={1,0,-1,-1,-1,0,1,1};

int bfs(){
	int wx=-1,wy=-1;
	rep(i,19)rep(j,15) stone[i][j]=-1;
	rep(i,19)rep(j,15){
		if(board[i][j]=='X')
			stone[i][j]=num++;
		if(board[i][j]=='O')
			wx=i,wy=j;
	}
	state init(0,wx,wy,(1<<20)-1);
	queue<state> q;
	q.push(init);
	while(!q.empty()){
		state cur=q.front();q.pop();
		int cost,cx,cy,mask;
		tie(cost,cx,cy,mask)=cur;
		if(cx>=18)
			return cost;
		rep(i,8){
			int nx=cx,ny=cy,nmask=mask;
			while(0<=nx+dx[i]&&nx+dx[i]<19&&0<=ny+dy[i]&&ny+dy[i]<15){
				int index=stone[nx+dx[i]][ny+dy[i]];
				if(index!=-1&&(nmask&(1<<index))){
					nmask&=(~(1<<index));
					nx+=dx[i],ny+=dy[i];
				}else
					break;
			}
			if(nx==cx&&ny==cy) continue;
			state nexts(cost+1,nx+dx[i],ny+dy[i],nmask);
			q.push(nexts);
		}
	}
	return -1;
}


int main(void){
	rep(i,19) cin >> board[i];
	cout << bfs() << endl;
	return 0;
}

