#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

struct S {
	int x, y, mask;
	ll hash() {
		return (ll)x + 15 * (ll)y + (ll)mask * (20 * 20);
	}
};

int b[19][15];

int dx[8] = { 1, 0, -1, 0, 1, 1, -1, -1 };
int dy[8] = { 0, 1, 0, -1, 1, -1, 1, -1 };

bool in(int x, int y) {
	return 0 <= x && x < 15 && 0 <= y && y < 19;
}

map<ll, int> dp;

int main() {
	int B = 0;
	int sx = -1, sy = -1;

	memset(b, -1, sizeof b);
	for(int i = 0; i < 19; i++) {
		string s;
		cin >> s;
		for(int j = 0; j < 15; j++) {
			if(s[j] == 'O') sx = j, sy = i;
			if(s[j] == 'X') {
				b[i][j] = B++;
			}
		}
	}

	S is = { sx, sy, 0 };
	dp[is.hash()] = 0;

	queue<S> q;
	q.push(is);
	while(q.size()) {
		int x = q.front().x, y = q.front().y, mask = q.front().mask;
		int t = dp[q.front().hash()];
		q.pop();
		for(int k = 0; k < 8; k++) {
			int nx = x + dx[k], ny = y + dy[k];
			if(!in(nx, ny)) continue;
			int id = b[ny][nx];
			if(id == -1) continue;
			if(mask >> id & 1) continue;
			int nmask = mask;
			while(1) {
