		for(int j = 0; j < 15; j++) {
			if(s[j] == 'O') sx = j, sy = i;
			if(s[j] == 'X') {
				b[i][j] = B++;
			}
		}
	}

	S is = { sx, sy, 0 };
	dp[is.hash()] = 0;

	queue<S> q;
	q.push(is);
	while(q.size()) {
		int x = q.front().x, y = q.front().y, mask = q.front().mask;
		int t = dp[q.front().hash()];
		q.pop();
		for(int k = 0; k < 8; k++) {
