    que.emplace_back(0,(1<<B.size())-1,ox,oy);
    while(!que.empty()){
        int d,bb,x,y;
        tie(d,bb,x,y)=que.front(); que.pop_front();
        // cout<<d<<' '<<bb<<' '<<x<<' '<<y<<endl;
        if(y>=18){
            cout<<d<<endl;
            return 0;
        }
        if(x<0||x==15) continue;
        for(int dy=-1;dy<=1;dy++){
            for(int dx=-1;dx<=1;dx++){
                if(!dx&&!dy)continue;
                if(x+dx<0||x+dx>=15) continue;
                if(y+dy<0||y+dy>=19) continue;
                if(board[x+dx][y+dy]==-1) continue;
                else{
                    if(bb&(1<<board[x+dx][y+dy])){
                        int l=2;
                        int bbb=bb^(1<<board[x+dx][y+dy]);
                        while(true){
                            if(x+dx*l<0||x+dx*l>=15||y+dy*l<0||y+dy*l>=19){
                                break;
                            }else{
                                if(board[x+dx*l][y+dy*l]==-1){
                                    break;
                                }else{
                                    if((1<<board[x+dx*l][y+dy*l])&bbb){
                                        bbb^=1<<board[x+dx*l][y+dy*l];
                                        l++;
                                        continue;
                                    }else{
                                        break;
                                    }
                                }
                            }
                        }
                        if(M.find(T(bbb,x+dx*l,y+dy*l))==M.end()){
                            M[T(bbb,x+dx*l,y+dy*l)]=d+1;
                            que.emplace_back(d+1,bbb,x+dx*l,y+dy*l);
                        }else if(M[T(bbb,x+dx*l,y+dy*l)]>d+1){
                            M[T(bbb,x+dx*l,y+dy*l)]=d+1;
                            que.emplace_back(d+1,bbb,x+dx*l,y+dy*l);
                        }
                    }
                }
            }
        }

    }
    cout<<-1<<endl;
    return 0;
}

