int Y[8]={-1,0,1,0,1,-1,1,-1};
int ans=inf;

bool in(int h,int w)
{
  if(h<0 || w<0 || 15<=w || 21<=h)return false;
  return true;
}

void move(int h,int w,int sum)
{
  bool check[8]={};
  if(18<=h){
    ans=min(ans,sum);
    return;
    }
  for(int k=0;k<8;k++){
    if(kiban[h+Y[k]][w+X[k]]=='.')continue;
    for(int i=1;i<20;i++){
      int a=h+Y[k]*i,b=w+X[k]*i;
      if(!in(a,b))break;
      if(kiban[a][b]=='.'){
	check[k]=true;
	break;
      }
    }
  }
  for(int k=0;k<8;k++){
    if(!check[k])continue;
    int a=h,b=w;
    for(int i=1;i<20;i++){
      a+=Y[k];
      b+=X[k];
      if(kiban[a][b]=='.')break;
      kiban[a][b]='.';
    }
    move(a,b,sum+1);
    int H=h,W=w;
    for(int i=1;i<20;i++){
      H+=Y[k];
      W+=X[k];
      if(H==a && W==b)break;
      kiban[H][W]='x';
    }
  }
}

