#include <bits/stdc++.h>
#define INF 100
using namespace std;
const int w = 15;
const int h = 19;
typedef pair<int,int> P;
vector<string> mp(h);
map<P,int>M;
bool inmp(int x,int y){return 0<=x&&0<=y&&x<w&&y<h;}
bool goal(int x,int y){return (y==h||(y==h-1&&0<=x&&x<w));}

int mem[1<<20],used[1<<20];
int dfs(int x,int y,int bit){
  if(goal(x,y)) return 0;
  if(!inmp(x,y)) return INF;
  if(used[bit]++) return mem[bit];

  int res = INF;
  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++){
      int nx = x+i;
      int ny = y+j;
      int nbit = bit;
      if(!inmp(nx,ny)||mp[ny][nx] != 'X') continue;

      vector<string>tmp = mp;
      while(inmp(nx,ny)&&mp[ny][nx]=='X'){
	mp[ny][nx] = '.';
	nbit |=1<<M[P(nx,ny)];
	nx+=i,ny+=j;
      }
      res=min(res,1+dfs(nx,ny,nbit));
      mp = tmp;
    }
  return mem[bit]=res;
}

int main(){

  for(int i=0;i<h;i++)cin>>mp[i];

  for(int i=0,c=0;i<h;i++)
    for(int j=0;j<w;j++) if(mp[i][j]=='X') M[P(j,i)] = c++;

  int ans; 
  for(int i=0;i<h;i++)
    for(int j=0;j<w;j++) if(mp[i][j] =='O') ans=dfs(j,i,0);

  cout<<(ans<INF? ans:-1)<<endl;
  return 0;
}

