int dx[] = {0, 0, 1, -1, 1, 1, -1, -1};
int dy[] = {1, -1, 0, 0, 1, -1, 1, -1};
const ll INF = 1001001001001001LL;
const ll MOD = 1000000007LL;

const int H = 19;
const int W = 15;
char board[H+2][W+2];

int solve(int x, int y) {
    if(x == H - 1 and 0 <= y and y < W) return 0;
    if(x > H - 1) return 0;
    if(y < 0 or y >= W or x < 0) return INF;
    assert(board[x][y] == '.');

    int ans = INF;
    for(int k=0; k<8; k++) {
        int nx = x + dx[k], ny = y + dy[k];
        if(nx < 0 or nx >= H or ny < 0 or ny >= W) continue;
        if(board[nx][ny] != 'X') continue;

        vector< pair<int, int> > black_pos;
        while(nx >= 0 and nx < H and ny >= 0 and ny < W and board[nx][ny] == 'X') {
            black_pos.emplace_back(nx, ny);
            board[nx][ny] = '.';
            nx += dx[k];
            ny += dy[k];
        }

        int nxt_val = solve(nx, ny);
        for(auto b : black_pos) {
            int bx, by; tie(bx, by) = b;
            board[bx][by] = 'X';
        }

        if(nxt_val == INF) continue;
        ans = min(ans, nxt_val + 1);
    }
    return ans;
}

signed main() {
    int x = -1, y = -1;
    for(int i=0; i<H; i++) {
        for(int j=0; j<W; j++) {
            cin >> board[i][j];
            if(board[i][j] == 'O') {
                x = i, y = j;
                board[i][j] = '.';
            }
        }
    }

    int ans = solve(x, y);
    if(ans >= INF) ans = -1;
    cout << ans << endl;
    return 0;
}

