#include<bits/stdc++.h>
#define INF (1e9)
#define N 19
#define M 15
using namespace std;
typedef pair<int,int> P;
typedef pair<int,P> P1;
int sy,sx,cnt;
map<P1,int> memo;
map<P,int> num;
string s[N];
int dy[8]={-1,-1,-1,0,0,1,1,1};
int dx[8]={-1,0,1,-1,1,-1,0,1};

int dfs(int A,int y,int x){
  if(memo.count(P1(A,P(y,x))))return memo[P1(A,P(y,x))];
  if(N-1<=y)return 1;
  if(y<0||x<0||N<=y||M<=x)return INF;
  int res=INF;
  memo[P1(A,P(y,x))]=INF;
  for(int i=0;i<8;i++){
    int ny=y+dy[i],nx=x+dx[i],nA=A;
    if(ny<0||nx<0||N<=ny||M<=nx)continue;
    if(s[ny][nx]!='X')continue;
    while(0<=ny&&ny<N&&0<=nx&&nx<M&&s[ny][nx]=='X'){
      nA|=(1<<num[P(ny,nx)]);
      s[ny][nx]='.';
      ny+=dy[i]; nx+=dx[i];
    }
    res=min(res,dfs(nA,ny,nx));
    while(ny!=y||nx!=x){
      ny-=dy[i]; nx-=dx[i];
      s[ny][nx]='X';
    }
  }
  return memo[P1(A,P(y,x))]=res+1;
}

int main(){
  for(int i=0;i<N;i++){
    cin>>s[i];
    for(int j=0;j<M;j++){
      if(s[i][j]=='O'){
	sy=i,sx=j;
	s[i][j]='.';
      }
      if(s[i][j]=='X')
	num[P(i,j)]=cnt++;
    }
  }
  int ans=dfs(0,sy,sx);
  cout<<(ans==INF+1?-1:ans-1)<<endl;
  return 0;
}

#include<bits/stdc++.h>
using namespace std;

struct state{
  int bi,a,b;
  bool operator<(const state& s) const {
    if( bi == s.bi ){
      if( a == s.a ) return b < s.b;
      return a < s.a;
    }
    return bi < s.bi;
  }
};

int dx[]={0,1,0,-1,1,1,-1,-1};
int dy[]={1,0,-1,0,1,-1,1,-1};

map<state,int> dp;
char cie[55][55];
int fie[55][55];
int N,W,H;
bool check( int bi,int x,int y,int d,int &nst,int &nx,int &ny ){
  bool f = false;
  nst = bi;
  nx = x + dx[d];
  ny = y + dy[d];  
  if( nx < 0 || nx >= W || ny < 0 || ny >= H ) return false;
  if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
    f = true;      
    nst |= (1<<fie[nx][ny]);
  }

  while( f ){
    nx = nx + dx[d];
    ny = ny + dy[d];  
    if( y > H-1 || ( y==H-1 && W > x && x > 0 ) ) return true;
    if( nx < 0 || nx >= W || ny < 0 ) return false;
    if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
      nst |= (1<<fie[nx][ny]);
    } else
      break;
  }
  return f;
}

int solve(int bi,int x,int y){
  if( dp.count((state){bi,x,y}) != 0 ) return dp[(state){bi,x,y}];
  // cout << bitset<20>(bi) << " "<< x << " "<< y << endl;
  if( y > H-1 || ( y==H-1 && W > x && x > 0 ) ) return dp[(state){bi,x,y}] = 0;
  int ret = (1<<29);
  for(int i=0;i<8;i++){
    int nx,ny,nst;
    if( check(bi,x,y,i,nst,nx,ny) ){
      ret = min( ret, solve(nst,nx,ny)+1);
    }
  }
  return dp[(state){bi,x,y}] = ret;
}

int main(){
  N = 0;
  W=15;H=19;
  int x,y;
  for(int i=0;i<19;i++){
    for(int j=0;j<15;j++){
      cin >> cie[j][i];
      if( cie[j][i] == 'X')
	fie[j][i] = N++;
      if( cie[j][i] == 'O' ){
	x = j; y = i;
      }	
    }
  }
  int res = solve(0,x,y);
  if( res == (1<<29) )
    cout << -1 << endl;
  else 
    cout << res << endl;
}

