      if (d[i][j] == 'O') {
        d[i][j] = '.';
        x = j, y = i;
        i = 19,j = 16;
      }
    }
  }
  int ans = solve(x, y, 0);
  if (ans == INF) ans = -1;
  printf("%d\n", ans);
}

