#include<bits/stdc++.h>
using namespace std;

struct state{
  int bi,a,b;
  bool operator<(const state& s) const {
    if( bi == s.bi ){
      if( a == s.a ) return b < s.b;
      return a < s.a;
    }
    return bi < s.bi;
  }
};

int dx[]={0,1,0,-1,1,1,-1,-1};
int dy[]={1,0,-1,0,1,-1,1,-1};


map<state,int> dp;
char cie[55][55];
int fie[55][55];
int N,W,H;
bool check( int bi,int x,int y,int d,int &nst,int &nx,int &ny ){
  bool f = false;
  nst = bi;
  nx = x + dx[d];
  ny = y + dy[d];  
  if( nx < 0 || nx >= W || ny < 0 || ny >= H ) return false;
  if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
    f = true;      
    nst |= (1<<fie[nx][ny]);
  }

  while( f ){
    nx = nx + dx[d];
    ny = ny + dy[d];  
    if( ny > H-1 || ( ny == H-1 && 0 <= nx && nx < W ) ) return true;
    if( nx < 0 || nx >= W || ny < 0 ) return false;
    if( cie[nx][ny] == 'X' && !(nst & (1<<fie[nx][ny])) ){
      nst |= (1<<fie[nx][ny]);
    } else
      break;
  }
  return f;
}

int solve(int bi,int x,int y){
  if( dp.find((state){bi,x,y}) != dp.end() ) return dp[(state){bi,x,y}];
  // cout << bitset<20>(bi) << " "<< x << " "<< y << endl;
  if( y > H-1 || ( y == H-1 && 0 <= x && x < W ) ) return dp[(state){bi,x,y}] = 0;
  int ret = (1<<29);
  for(int i=0;i<8;i++){
    int nx,ny,nst;
    if( check(bi,x,y,i,nst,nx,ny) ){
      ret = min( ret, solve(nst,nx,ny)+1);
    }
  }
  return dp[(state){bi,x,y}] = ret;
}

int main(){
  N = 0;
  W=15;H=19;
  int x,y;
  for(int i=0;i<19;i++){
    for(int j=0;j<15;j++){
      cin >> cie[j][i];
      if( cie[j][i] == 'X')
	fie[j][i] = N++;
      if( cie[j][i] == 'O' ){
	x = j; y = i;
      }	
    }
  }
  int res = solve(0,x,y);
  if( res == (1<<29) )
    cout << -1 << endl;
  else 
    cout << res << endl;
}

