constexpr size_t m1 = -1;
constexpr size_t di[] = {m1, m1, m1, 0, 0, 1, 1, 1};
constexpr size_t dj[] = {m1, 0, 1, m1, 1, m1, 0, 1};

template <class Tp>
constexpr Tp inf = Tp(1) << (8*sizeof(Tp)-3);

int dfs(std::vector<std::string>& s, size_t i, size_t j) {
  // char tmp = s[i][j];
  // s[i][j] = '@';
  // for (const auto& si: s)
  //   fprintf(stderr, "%s\n", si.c_str());
  // fprintf(stderr, "---\n");
  // s[i][j] = tmp;

  int res = inf<int>;
  for (int k = 0; k < 8; ++k) {
    size_t ni = i + di[k];
    size_t nj = j + dj[k];
    if (!(ni < H && nj < W && s[ni][nj] == 'X')) continue;

    std::vector<std::pair<size_t, size_t>> jmp;
    do {
      jmp.emplace_back(ni, nj);
      ni += di[k];
      nj += dj[k];
      if (ni == H) return 1;
      if (ni+1 == H && nj < W) return 1;
      if (!(ni < H && nj < W)) break;  // cannot jump
      if (s[ni][nj] != 'X') {
        for (const auto& p: jmp) {
          size_t ji, jj;
          std::tie(ji, jj) = p;
          s[ji][jj] = '.';
        }
        res = std::min(res, 1 + dfs(s, ni, nj));
        for (const auto& p: jmp) {
          size_t ji, jj;
          std::tie(ji, jj) = p;
          s[ji][jj] = 'X';
        }
        break;
      }
    } while (true);
  }
  return res;
}

