using namespace std;

const int IINF = INT_MAX;
const int h = 19;
const int w = 15;

struct Data { int x,y,bitmask; };

char a[20][20];
int mindist[1<<20];
int Index[20][20];

bool isValid(int x,int y) { return 0 <= x && x < w && 0 <= y && y < h; }

void compute(){
  int black = 0;
  int sx=-1,sy=-1;
  rep(i,h) rep(j,w) {
    if( a[i][j] == 'X' ) Index[i][j] = black++;
    if( a[i][j] == 'O' ) sx = j, sy = i;
  }
  rep(i,(1<<black)) mindist[i] = IINF;
  mindist[(1<<black)-1] = 0;
  deque<Data> deq;
  deq.push_back((Data){sx,sy,(1<<black)-1});
  while( !deq.empty() ){
    Data data = deq.front(); deq.pop_front();
    REP(dx,-1,2){
      REP(dy,-1,2){
        if( dx == 0 && dy == 0 ) continue;
        int nx = data.x + dx, ny = data.y + dy, nbitmask = data.bitmask;
        bool valid = false;
        while( isValid(nx,ny) && ( a[ny][nx] == 'X' && ( data.bitmask & (1<<Index[ny][nx]) ) ) ){
          nbitmask &= ~(1<<Index[ny][nx]);
          nx += dx, ny += dy;
          valid = true;
        }
        if( !isValid(nx,ny) ) {
          if( valid && ( 0 <= nx && nx < w ) && ny >= h ) {
            cout << mindist[data.bitmask]+1 << endl;
            return;
          }
          continue;
        }
        if( !valid ) continue;
        if( mindist[nbitmask] > mindist[data.bitmask] + 1 ) {
          mindist[nbitmask] = mindist[data.bitmask] + 1;
          deq.push_back((Data){nx,ny,nbitmask});
        }
      }
    }
  }
  puts("-1");
}

int main(){
  rep(i,h) rep(j,w) scanf(" %c",&a[i][j]);
  compute();
  return 0;
}

