const int dx[8] = { 0, 1, 1, 1, 0, -1, -1, -1 };
const int dy[8] = { 1, 1, 0, -1, -1, -1, 0, 1 };

int BFS(vector<string> v)
{
	map<vector<string>, int> d; d[v] = 1;

	queue<pair<vector<string>, int> > que; que.push(make_pair(v, 1));

	for (int i = 0; i < 19; i++)
	{
		if (v[14][i] == 'O')
		{
			return 0;
		}
	}

	while (!que.empty())
	{
		vector<string> v1 = que.front().first;

		int dist = que.front().second; que.pop();

		int sx = -1, sy = -1;

		for (int i = 0; i < 15; i++)
		{
			for (int j = 0; j < 19; j++)
			{
				if (v1[i][j] == 'O')
				{
					sx = j; sy = i;
				}
			}
		}

		for (int dir = 0; dir < 8; dir++)
		{
			if (0 <= sx + dx[dir] && sx + dx[dir] < 19 && 0 <= sy + dy[dir] && sy + dy[dir] < 15)
			{
				if (v1[sy + dy[dir]][sx + dx[dir]] == 'X')
				{
					int x = sx + dx[dir] * 2;
					int y = sy + dy[dir] * 2;

					while (0 <= x && x < 19 && 0 <= y)
					{
						if (y > 13) { return dist; }

						if (v1[y][x] == '.')
						{
							vector<string> v2 = v1;

							int rx = sx, ry = sy;

							for (; x != rx || y != ry;)
							{
								v2[ry][rx] = '.';

								rx += dx[dir];
								ry += dy[dir];
							}

							if (d[v2] == 0)
							{
								d[v2] = dist + 1;

								que.push(make_pair(v2, dist + 1));

								goto Exit;
							}
						}

						x += dx[dir];
						y += dy[dir];
					}
				}
			}

		Exit:;
		}
	}

	return -1;
}

int main()
{
	vector<string> v(15);

	for (int i = 0; i < 15; i++) { cin >> v[i]; }

	cout << BFS(v) << endl;

	return 0;
}

