typedef tuple<pair<char, char>, vector<vector<bool>>, char> T;
queue<T> q;
map<vector<vector<bool>>, vector<vector<char>>> memo;

const int dy[16] = { 0,-1, 0, 1, 1,-1, 1,-1, 0,-2, 0, 2};
const int dx[16] = { 1, 0,-1, 0, 1, 1,-1,-1, 2, 0,-2, 0};

bool impossible(int y, vector<vector<bool>>& c){
	vector<bool> no(h,0);
	range(i,y + 1,18){
		bool f = false;
		rep(j,w){
			if(c[i][j]) f = true;
		}
		if(not f) no[i] = true;
	}
	rep(i,h - 1){
		if(no[i] and no[i + 1]) return true;
	}
	return false;
}

int main(){
	c = vector<vector<bool>>(h, vector<bool>(w,0));

	pair<char, char> s;
	rep(i,h - 1){
		rep(j,w - 2){
			char a;
			cin >> a;
			if(a == 'X') c[i][j + 1] = 1;
			else c[i][j + 1] = 0;
			if(a == 'O'){
				s = make_pair(i,j + 1);
			}
		}
	}

	q.push(T(s,c,0));

	char ans = 127;
	while(not q.empty()){
		char cost;
		tie(s,c,cost) = q.front(); q.pop();

		char y, x;
		tie(y,x) = s;

		//rep(i,h){ rep(j,w){ if(i == y and j == x) cout << 'O'; else cout << c[i][j]; } cout << endl; } cout << endl;

		if(y == 18 or y == 19){
			ans = min(ans, cost);
			continue;
		}

		if(impossible(y, c)) continue;

		if(cost >= 20) continue;

		rep(i,8){
			char ny = y + dy[i];
			char nx = x + dx[i];
			if(ny < 0 || ny >= h || nx < 0 || nx >= w) continue;
			if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)) continue;
			if(c[ny][nx] == 1){
				vector<pair<int, int>> tmp;
				while(true){
					if(ny < 0 || ny >= h || nx < 0 || nx >= w) {
						ny = -1;
						break;
					}
					if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)){
						ny = -1;
						break;
					}
					if(c[ny][nx] == 0) break;
					tmp.emplace_back(ny,nx);
					//pc[ny][nx] = 0;
					ny = ny + dy[i];
					nx = nx + dx[i];
				}
				if(ny == -1) continue;

				for(auto i : tmp) c[i.first][i.second] = 0;
				if(memo.count(c)){
					auto& it = memo[c];
					if(it[ny][nx] > cost + 1){
						it[ny][nx] = cost + 1;
						q.push(T(make_pair(ny,nx), c, cost + 1));
					}
				}else{
					vector<vector<char>> dis(h, vector<char>(w,127));
					dis[ny][nx] = cost + 1;
					memo[c] = dis;
					q.push(T(make_pair(ny,nx), c, cost + 1));
				}
				for(auto i : tmp) c[i.first][i.second] = 1;
			}
		}
	}
	cout << (ans == 127 ? -1 : ans) << endl;
}

