const int H = 19;
const int W = 15;
string bd[H];
int id[H][W];
int dx[] = {1,1,0,-1,-1,-1,0,1};
int dy[] = {0,1,1,1,0,-1,-1,-1};

struct PH{
  template<class T, class U>
  size_t operator()(const pair<T,U>& x) const{
	size_t seed = 0;
	seed ^= hash<T>{}(x.first) + 0x9e3779b9 + (seed<<6) + (seed>>2);
	seed ^= hash<U>{}(x.second) + 0x9e3779b9 + (seed<<6) + (seed>>2);
	return seed;
  }
};

using D = pair<PII,int>;
namespace std{
template<> struct hash<D>{
  size_t operator()(const D& d) const{
	return PH{}(MP(PH{}(d.FF), d.SS));
  }
};
}

bool isin(const PII& p){
  return (0 <= p.FF && p.FF < H && 0 <= p.SS && p.SS < W);
}


int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  REP(i,19) cin >> bd[i];
  PII w;
  int gid = 0;
  REP(y,H) REP(x,W){
	if(bd[y][x] == 'O')
	  w = MP(y,x);
	else if(bd[y][x] == 'X')
	  id[y][x] = gid++;
  }

  int ans = -1;
  map<D,int> memo;
  queue<D> q;
  q.push(D(w,(1<<21)-1));
  memo[D(w,(1<<21)-1)] = 0;
  while(!q.empty()){
	D d = q.front();
	q.pop();
	PII p = d.FF;
	if(p.FF >= H || (p.FF == H-1 && isin(p))){
	  ans = memo[d];
	  break;
	}
	if(p.FF < 0 || p.SS < 0 || p.SS >= W)
	  continue;
	REP(dir,8){
	  int nb = d.SS;
	  PII np(p.FF+dy[dir], p.SS+dx[dir]);
	  while(isin(np) && bd[np.FF][np.SS] == 'X' && (nb>>id[np.FF][np.SS]&1)){
		nb ^= 1 << id[np.FF][np.SS];
		np.FF += dy[dir];
		np.SS += dx[dir];
	  }
	  if(nb == d.SS) continue;
	  if(!memo.count(D(np,nb))){
		 memo[D(np,nb)] = memo[d] + 1;
		 q.push(D(np,nb));
	  }
	}
  }
  cout << ans << endl;

  return 0;
}

