using namespace std;
struct State { int x[19][15]; };
State S;
int dx[8] = { 1,1,1,0,-1,-1,-1,0 }, dy[8] = { -1,0,1,1,1,0,-1,-1 };
int solve(State p) {
	int cx = 0, cy = 0;
	for (int i = 0; i < 285; i++) {
		if (p.x[i / 15][i % 15] == 2) { cx = i / 15; cy = i % 15; }
	}
	int minx = 999999;
	for (int i = 0; i < 8; i++) {
		State T = p;
		int ex = cx, ey = cy;
		bool flag = true;
		while (true) {
			ex += dx[i]; ey += dy[i];
			if (ex >= 19)break;
			if (ex < 0 || ey < 0 || ey >= 15) { flag = false; break; }
			if (T.x[ex][ey] == 0)break;
			T.x[ex][ey] = 0;
		}
		if (flag == false)continue;

		if (abs(ex - cx) <= 1 && abs(ey - cy) <= 1)continue;
		if (ex == 18 || ex == 19)minx = min(minx, 1);
		else {
			T.x[cx][cy] = 0; T.x[ex][ey] = 2;
			minx = min(minx, solve(T) + 1);
		}
	}
	return minx;
}
int main() {
	for (int i = 0; i < 19; i++) {
		for (int j = 0; j < 15; j++) {
			char p; cin >> p;
			if (p == 'O')S.x[i][j] = 2;
			if (p == 'X')S.x[i][j] = 1;
			if (p == '.')S.x[i][j] = 0;
		}
	}
	int R = solve(S); if (R >= 100000)R = -1;
	cout << R << endl;
	return 0;
}

