	for (int i = 1; i <= H+1; i++){
		for (int j = 1; j <= W; j++){
			if (i == H + 1) masu[i][j] = '.';
			else {
				cin >> masu[i][j];
				if (masu[i][j] == 'O') { sx = i; sy = j; masu[i][j] = '.'; }
			}
		}
	}

	int f = 0;
	for (int i = sx + 1; i <= H; i++) {
		for (int j = 1; j <= W;j++) {
			if (masu[i][j] == 'X') { f = 0; break; }
			if(j == W)f++;
		}
		if (f == 2) {
			cout << -1 << endl; return 0;
		}
	}

	dfs(sx, sy, 0, masu);
	cout << (ans == INF ? -1 : ans) << endl;
	return 0;
}

