using namespace std;

#define reps(i,f,n) for(int i=f;i<int(n);i++)
#define rep(i,n) reps(i,0,n)

const int N = 20, T = (1<<N), H = 19, W = 15;

set<int> memo[T];
int board[H][W];
int stx,sty;

class Q{
public:
	int x,y,cnt,state;
	Q(int x,int y,int cnt, int state):x(x),y(y),cnt(cnt),state(state){}
};

void input(){
	string a[H];

	rep(i,H)cin>>a[i];
	int cnt = 0;
	rep(i,H){
		rep(j,W){
			board[i][j] = -1;
			if(a[i][j] == 'X'){
				board[i][j] = cnt;
				cnt++;
			}
			if(a[i][j] == 'O'){
				stx = j; sty = i;
			}
		}
	}
}


int zahyo(int x,int y){
	return x + y * W;
}

bool hasmemo(Q u){
	return memo[u.state].find(zahyo(u.x,u.y)) != memo[u.state].end();
}

void setmemo(Q u){
	memo[u.state].insert(zahyo(u.x,u.y));
}

bool hasbit(int state, int i){
	return (state&(1<<i))>0;
}

bool isblack(int x,int y,int state){
	int n = board[y][x];
	if(n<0)return false;
	return !hasbit(state,n);
}

bool isout(int x,int y){
	if(y<0)return true;
	if(y<H){
		if(x<0 || x>=W)return true;
	}
	return false;
}

bool isgoal(int x,int y){
	if(y>=H) return true;
	return false;
}


int minblack(int x,int y,int state){
	int n = board[y][x];
	//if(n<0)puts("hoooo");
	//if(hasbit(state,n))puts("hhahah");

	return state + (1<<n);
}

int solve(){

	queue<Q> que;
	que.push(Q(stx,sty,0,0));

	while(!que.empty()){
		Q u = que.front(); que.pop();
		if(hasmemo(u))continue;
		setmemo(u);

		//printf("%d %d\n",u.x,u.y);

		int dx[] = {-1,0,1,-1,1,-1,0,1};
		int dy[] = {-1,-1,-1,0,0,1,1,1};

		rep(p,8){
			int st = u.state;
			reps(i,1,100){
				int nx = u.x + dx[p]*i;
				int ny = u.y + dy[p]*i;


				if(i==1){
					if(isout(nx,ny) || isgoal(nx,ny))break;
					if(!isblack(nx,ny,st))break;
				}
				if(i!=1){
					if(isout(nx,ny))break;
					if(isgoal(nx,ny))return u.cnt+1;
					if(!isblack(nx,ny,st)){
						que.push(Q(nx,ny,u.cnt+1,st));
						break;
					}
				}

				st = minblack(nx,ny,st);
			}
		}
	}
	return -1;
}

int main(){
	input();
	cout<<solve()<<endl;
}

#include <bits/stdc++.h>
#define REP(i, a, n) for(ll i = ((ll) a); i < ((ll) n); i++)
using namespace std;
typedef long long ll;
typedef pair<ll, ll> pll;

int main(void) {
  const ll H = 19, W = 15;
  char S[H][W];
  REP(i, 0, H) REP(j, 0, W) cin >> S[i][j];

  pll white;
  vector<pll> black, cand;
  REP(i, 0, H) REP(j, 0, W) {
    if(S[i][j] == 'O') {
      white = pll(i, j);
      cand.push_back(pll(i, j));
    }
    if(S[i][j] == 'X') {
      black.push_back(pll(i, j));
      REP(di, -1, 2) REP(dj, -1, 2) if(di != 0 || dj != 0) {
        ll ni = i + di, nj = j + dj;
        if(!(0 <= ni && ni < H && 0 <= nj && nj < W)) break;
        cand.push_back(pll(i + di, j + dj));
      }
    }
  }
  sort(black.begin(), black.end());
  sort(cand.begin(), cand.end());
  cand.erase(unique(cand.begin(), cand.end()), cand.end());

  ll N = black.size(), M = cand.size();
  map<pll, bool> visited;

  struct state { ll s, v, d; };
  queue<state> q;
  q.push((state) { (1LL << N) - 1, lower_bound(cand.begin(), cand.end(), white) - cand.begin(), 0 });

  while(!q.empty()) {
    ll s = q.front().s, v = q.front().v, d = q.front().d;
    ll y = cand[v].first, x = cand[v].second;
    q.pop();

    visited[pll(s, v)] = true;
    REP(i, 0, N) cout << (s & (1LL << i) ? 1 : 0);

    REP(i, -1, 2) REP(j, -1, 2) if(i != 0  || j != 0) {
      ll ns = s, ny = y + i, nx = x + j, nd = d + 1;
      ll cnt = 0;

      while(1) {
        if(!(0 <= ny && ny < H && 0 <= nx && nx < W)) break;
        if(S[ny][nx] != 'X') break;

        ll b = lower_bound(black.begin(), black.end(), pll(ny, nx)) - black.begin();
        if(b == N) break;
        if(!(ns & (1LL << b))) break;

        ns = ns - (1LL << b);
        ny += i;
        nx += j;
        cnt++;
      }

      if(cnt > 0 && ny >= H - 1) {
        cout << nd << endl;
        return 0;
      } else if(cnt > 0 && 0 <= ny && ny < H && 0 <= nx && nx < W) {
        ll nv = lower_bound(cand.begin(), cand.end(), pll(ny, nx)) - cand.begin();
        if(!visited[pll(ns, nv)]) q.push((state) { ns, nv, nd });
      }
    }
  }
  cout << -1 << endl;
}

