const int INF = 1e8;
using namespace std;

const int h = 20, w = 17;
vector<vector<char>> c;

const int dy[16] = { 0,-1, 0, 1, 1,-1, 1,-1, 0,-2, 0, 2};
const int dx[16] = { 1, 0,-1, 0, 1, 1,-1,-1, 2, 0,-2, 0};

int main(){
	c = vector<vector<char>>(h, vector<char>(w,'.'));

	pair<int, int> s;
	rep(i,h - 1){
		rep(j,w - 2){
			cin >> c[i][j + 1];
			if(c[i][j + 1] == 'O') s = make_pair(i,j + 1);
		}
	}

	typedef tuple<pair<int, int>, vector<vector<char>>, int> T;
	queue<T> q;
	q.push(T(s,c,0));

	int ans = INF;
	map<vector<vector<char>>, vector<vector<int>>> memo;
	while(not q.empty()){
		int cost;
		tie(s,c,cost) = q.front(); q.pop();

		int y, x;
		tie(y,x) = s;

		//rep(i,h){ rep(j,w){ if(i == y and j == x) cout << 'O'; else cout << c[i][j]; } cout << endl; } cout << endl;

		if(y == 18 or y == 19){
			ans = min(ans, cost);
			continue;
		}

		rep(i,8){
			int ny = y + dy[i];
			int nx = x + dx[i];
			if(ny < 0 || ny >= h || nx < 0 || nx >= w) continue;
			if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)) continue;
			vector<vector<char>> pc = c;
			if(pc[ny][nx] == 'X'){
				while(true){
					if(ny < 0 || ny >= h || nx < 0 || nx >= w) {
						ny = -1;
						break;
					}
					if((nx == 0 && ny <= 18) or (nx == w - 1 && ny <= 18)){
						ny = -1;
						break;
					}
					if(pc[ny][nx] == '.') break;
					pc[ny][nx] = '.';
					ny = ny + dy[i];
					nx = nx + dx[i];
				}
				if(ny == -1) continue;

				if(memo.count(pc)){
					auto& it = memo[pc];
					if(it[ny][nx] > cost + 1){
						it[ny][nx] = cost + 1;
						q.push(T(make_pair(ny,nx), pc, cost + 1));
					}
				}else{
					vector<vector<int>> dis(h, vector<int>(w,INF));
					dis[ny][nx] = cost + 1;
					memo[pc] = dis;
					q.push(T(make_pair(ny,nx), pc, cost + 1));
				}
			}
		}
	}
	cout << (ans == INF ? -1 : ans) << endl;
}

#include <bits/stdc++.h>
#define FOR(i,k,n) for(int i=(k);i<int(n);++i)
#define REP(i,n) FOR(i,0,n)
#define ALL(x) begin(x),end(x)

using namespace std;
constexpr int INF = 30;

map<pair<int,int>, int8_t> memo;

int8_t dfs(const vector<vector<int>> &idx, int i, int j) {
  auto itr = memo.find(make_pair(i, j));
  if (itr != end(memo)) return itr->second;
  if (j == 15*19) return 0;
  int oi = j/15;
  int oj = j%15;
  int di[8] = {1, 1, 1, 0, -1, -1, -1, 0};
  int dj[8] = {-1, 0, 1, 1, 1, 0, -1, -1};
  int8_t res = INF;
  REP(k,8) {
    int l = 1;
    vector<int> ei;
    int ni;
    int nj;
    while (1) {
      ni = oi + l*di[k];
      nj = oj + l*dj[k];
      if (ni < 0 || ni >= 19 || nj < 0 || nj >= 15) break;
      if (idx[ni][nj] < 0 || ((i>>idx[ni][nj])&1)) break;
      ei.push_back(idx[ni][nj]);
      ++l;
    }
    if (l > 1) {
      int pi = i;
      for (int ix : ei) {
        pi |= 1 << ix;
      }
      if (ni >= 19 || (ni == 18 && nj >= 0 && nj < 15)) {
        res = min(res, int8_t(dfs(idx, pi, 15*19)+1));
      } else if (ni >= 0 && nj >= 0 && nj < 15) {
        res = min(res, int8_t(dfs(idx, pi, ni*15+nj)+1));
      }
    }
  }
  memo[make_pair(i,j)] = res;
  return res;
}

int main() {
  vector<string> t(19);
  vector<pair<int, int>> vb;
  vector<vector<int>> idx(19, vector<int>(15, -1));
  int si, sj;
  REP(i,19) {
    cin>>t[i];
    REP(j,15) {
      if (t[i][j] == 'X') {
        idx[i][j] = vb.size();
        vb.emplace_back(i,j);
      } else if (t[i][j] == 'O') {
        si = i;
        sj = j;
      }
    }
  }
  int res = dfs(idx, 0, si*15+sj);
  if (res < INF) {
    cout << (int)res << endl;
  } else {
    cout << -1 << endl;
  }
  return 0;
}

#include<iostream>
#include<algorithm>
#include<cmath>
using namespace std;
struct State { int x[19][15]; };
State S;
int dx[8] = { 1,1,1,0,-1,-1,-1,0 }, dy[8] = { -1,0,1,1,1,0,-1,-1 };
int solve(State p) {
	int cx = 0, cy = 0;
	for (int i = 0; i < 285; i++) {
		if (p.x[i / 15][i % 15] == 2) { cx = i / 15; cy = i % 15; }
	}
	int minx = 999999;
	for (int i = 0; i < 8; i++) {
		State T = p;
		int ex = cx, ey = cy;
		bool flag = true;
		while (true) {
			ex += dx[i]; ey += dy[i];
			if (ex < 0 || ey < 0 || ey >= 15) { flag = false; break; }
			if (ex >= 19)break;
			if (T.x[ex][ey] == 0)break;
			T.x[ex][ey] = 0;
		}
		if (flag == false)continue;

		if (abs(ex - cx) <= 1 && abs(ey - cy) <= 1)continue;
		if (ex == 18 || ex == 19)minx = min(minx, 1);
		else {
			T.x[cx][cy] = 0; T.x[ex][ey] = 2;
			minx = min(minx, solve(T) + 1);
		}
	}
	return minx;
}
int main() {
	for (int i = 0; i < 19; i++) {
		for (int j = 0; j < 15; j++) {
			char p; cin >> p;
			if (p == 'O')S.x[i][j] = 2;
			if (p == 'X')S.x[i][j] = 1;
			if (p == '.')S.x[i][j] = 0;
		}
	}
	int R = solve(S); if (R >= 100000)R = -1;
	cout << R << endl;
	return 0;
}

