using namespace std;

#define reps(i,f,n) for(int i=f;i<int(n);i++)
#define rep(i,n) reps(i,0,n)

const int N = 20, T = (1<<N), H = 19, W = 15;

set<int> memo[T];
int board[H][W];
int stx,sty;

class Q{
public:
	int x,y,cnt,state;
	Q(int x,int y,int cnt, int state):x(x),y(y),cnt(cnt),state(state){}
};

void input(){
	string a[H];

	rep(i,H)cin>>a[i];
	int cnt = 0;
	rep(i,H){
		rep(j,W){
			board[i][j] = -1;
			if(a[i][j] == 'X'){
				board[i][j] = cnt;
				cnt++;
			}
			if(a[i][j] == 'O'){
				stx = j; sty = i;
			}
		}
	}
}


int zahyo(int x,int y){
	return x * W + y;
}

bool hasmemo(Q u){
	return memo[u.state].find(zahyo(u.x,u.y)) != memo[u.state].end();
}

void setmemo(Q u){
	memo[u.state].insert(zahyo(u.x,u.y));
}

bool hasbit(int state, int i){
	return (state&(1<<i))>0;
}

bool isblack(int x,int y,int state){
	int n = board[y][x];
	if(n<0)return false;
	return !hasbit(state,n);
}

bool isout(int x,int y){
	if(y<0)return true;
	if(y<H){
		if(x<0 || x>=W)return true;
	}
	return false;
}

bool isgoal(int x,int y){
	if(y>=H) return true;
	return false;
}


int minblack(int x,int y,int state){
	int n = board[y][x];
	if(n<0)puts("hoooo");
	if(hasbit(state,n))puts("hhahah");

	return state + (1<<n);
}

int solve(){

	queue<Q> que;
	que.push(Q(stx,sty,0,0));

	while(!que.empty()){
		Q u = que.front(); que.pop();
		if(hasmemo(u))continue;
		setmemo(u);

		//printf("%d %d\n",u.x,u.y);

		int dx[] = {-1,0,1,-1,1,-1,0,1};
		int dy[] = {-1,-1,-1,0,0,1,1,1};

		rep(p,8){
			int st = u.state;
			reps(i,1,100){
				int nx = u.x + dx[p]*i;
				int ny = u.y + dy[p]*i;


				if(i==1){
					if(isout(nx,ny) || isgoal(nx,ny))break;
					if(!isblack(nx,ny,st))break;
				}
				if(i!=1){
					if(isout(nx,ny))break;
					if(isgoal(nx,ny))return u.cnt+1;
					if(!isblack(nx,ny,st)){
						que.push(Q(nx,ny,u.cnt+1,st));
						break;
					}
				}

				st = minblack(nx,ny,st);
			}
		}
	}
	return -1;
}

int main(){
	input();
	cout<<solve()<<endl;
}

#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

struct S {
	int x, y, mask;
};

int b[19][15];
int dp[19][15][1 << 20];

int dx[8] = { 1, 0, -1, 0, 1, 1, -1, -1 };
int dy[8] = { 0, 1, 0, -1, 1, -1, 1, -1 };

bool in(int x, int y) {
	return 0 <= x && x < 15 && 0 <= y && y < 19;
}

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);

	int B = 0;
	int sx = -1, sy = -1;
	memset(b, -1, sizeof b);
	for(int i = 0; i < 19; i++) {
		string s;
		cin >> s;
		for(int j = 0; j < 15; j++) {
			if(s[j] == 'O') sx = j, sy = i;
			if(s[j] == 'X') {
				b[i][j] = B++;
			}
		}
	}

	const int INF = 1 << 25;
	fill((int*)begin(dp), (int*)end(dp), INF);
	dp[sy][sx][0] = 0;

	queue<S> q;
	q.push({ sx, sy, 0 });
	while(q.size()) {
		int x = q.front().x, y = q.front().y, mask = q.front().mask;
		int t = dp[y][x][mask];
		q.pop();
		for(int k = 0; k < 8; k++) {
			int nx = x + dx[k], ny = y + dy[k];
			if(!in(nx, ny)) continue;
			int id = b[ny][nx];
			if(id == -1) continue;
			if(mask >> id & 1) continue;
			//cerr << "nx : " << nx << ", ny : " << ny << endl;
			int nmask = mask;
			while(1) {
				nmask |= 1 << id;
				nx += dx[k], ny += dy[k];
				if(!in(nx, ny)) break;
				id = b[ny][nx];
				if(id == -1 || (mask >> id & 1)) break;
			}
			if(ny >= 19) {
				cout << t + 1 << endl;
				return 0;
			}
			if(in(nx, ny)) {
				if(dp[ny][nx][nmask] > t + 1) {
					dp[ny][nx][nmask] = t + 1;
					q.push({ nx, ny, nmask });
				}
			}
		}
	}
	cout << -1 << endl;
}

