      } else if (t[i][j] == 'O') {
        si = i;
        sj = j;
      }
    }
  }
  int n = vb.size();
  vector<vector<int8_t>> dp(1<<n, vector<int8_t>(15*19+1, INF));
  dp[0][si*15+sj] = 0;
  REP(i,1<<n) {
    REP(j,15*19) {
      if (dp[i][j] == INF) continue;
      int oi = j/15;
      int oj = j%15;
      int di[8] = {1, 1, 1, 0, -1, -1, -1, 0};
      int dj[8] = {-1, 0, 1, 1, 1, 0, -1, -1};
      REP(k,8) {
        int l = 1;
        vector<int> ei;
        int ni;
        int nj;
        while (1) {
          ni = oi + l*di[k];
          nj = oj + l*dj[k];
          if (ni < 0 || ni >= 19 || nj < 0 || nj >= 15) break;
          if (idx[ni][nj] < 0 || ((i>>idx[ni][nj])&1)) break;
          ei.push_back(idx[ni][nj]);
          ++l;
        }
        if (l > 1) {
          int pi = i;
          for (int ix : ei) {
            pi |= 1 << ix;
          }
          if (ni >= 19) {
            dp[pi][15*19] = min(dp[pi][15*19], int8_t(dp[i][j]+1));
          } else if (ni >= 0 && nj >= 0 && nj < 15) {
            dp[pi][ni*15+nj] = min(dp[pi][ni*15+nj], int8_t(dp[i][j]+1));
          }
        }
      }
    }
  }
  int8_t res = INF;
  REP(i,1<<n) res = min(res, dp[i][15*19]);
  if (res < INF) {
    cout << (int)res << endl;
  } else {
    cout << -1 << endl;
  }
  return 0;
}

#include <bits/stdc++.h>
#define INF 100
using namespace std;
const int w = 15;
const int h = 19;
typedef pair<int,int> P;
vector<string> mp(h);
map<P,int>M;
bool inmp(int x,int y){return 0<=x&&0<=y&&x<w&&y<h;}
bool goal(int x,int y){return (y==h||(y==h-1&&0<=x&&x<w));}

int mem[1<<20],used[1<<20];
int dfs(int x,int y,int bit){
  if(goal(x,y)) return 0;
  if(!inmp(x,y)) return INF;
  if(used[bit]++) return mem[bit];

  int res = INF;
  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++){
      int nx = x+i;
      int ny = y+j;
      int nbit = bit;
      if(!inmp(nx,ny)||mp[ny][nx] != 'X') continue;

      vector<string>tmp = mp;
      while(inmp(nx,ny)&&mp[ny][nx]=='X'){
	mp[ny][nx] = '.';
	nbit |=1<<M[P(nx,ny)];
	nx+=i,ny+=j;
      }
      res=min(res,1+dfs(nx,ny,nbit));
      mp = tmp;
    }
  return mem[bit]=res;
}

