const int inf = 1e9;
int dx[8] = {1, 1, 1, 0, 0,-1,-1,-1};
int dy[8] = {1, 0,-1, 1,-1, 1, 0,-1};
vector<vector<char> > field(21, vector<char>(17, '.'));
int solve(int sy, int sx){
    if(sy == 20 || (sy == 19 && 1 <= sx && sx <= 15)){
        return 0;
    }
    if(sy <= 0 || 20 <= sy || sx <= 0 || 16 <= sx){
        return inf;
    }
    int ret = inf;
    for(int i=0; i<8; i++){
        int ny = sy +dy[i];
        int nx = sx +dx[i];
        if(field[ny][nx] != 'X') continue;
        while(field[ny][nx] == 'X'){
            field[ny][nx] = '.';
            ny += dy[i];
            nx += dx[i];
        }
        ret = min(ret, solve(ny, nx));
        while(ny != sy || nx != sx){
            ny -= dy[i];
            nx -= dx[i];
            field[ny][nx] = 'X';
        }
        field[sy][sx] = '.';
    }
    return ret+1;
}

