int field[19][15];
struct aa {
	int x;
	int y;
	bitset<20>bs;
	int turn;
};
bool operator<(const aa&l, const aa&r) {
	return l.x == r.x ? l.y == r.y ? l.bs.to_ulong() < r.bs.to_ulong() : l.y < r.y : l.x < r.x;
}bool operator==(const aa&l, const aa&r) {
	return l.x == r.x && l.y == r.y && l.bs.to_ulong();
}

int dx8[8] = { -1,-1,-1, 0, 1, 1, 1, 0 };
int dy8[8] = { -1, 0, 1, 1, 1, 0,-1,-1 };
pair<int, int> amove(const int way,const int fx, const int fy, bitset<20>&bs) {
	int nx = fx+dx8[way];
	int ny = fy + dy8[way];
	bool ok = false;
	while (1) {
		if (ny >= 18 && ok) {
			break;
		}
		if (nx < 0 || nx >= 15 || ny < 0)return make_pair(-1, -1);
		if (field[ny][nx] && bs[field[ny][nx] - 1]) {
			bs[field[ny][nx] - 1] = false;
		}
		else {
			break;
		}
		if (ny >= 18&&ok) {
			break;
		}
		ok = true;
		ny += dy8[way];
		nx += dx8[way];
	}
	if (!ok) {
		return make_pair(-1, -1);
	}
	else {
		return make_pair(nx, ny);
	}
}

int main() {
	int num = 1;
	int sx = 0, sy = 0;
	for (int i = 0; i < 19; ++i) {
		string st; cin >> st;
		for (int j = 0; j < 15; ++j) {
			if (st[j] == 'X') {
				field[i][j] = num;
				num++;
			}
			else if (st[j] == 'O') {
				field[i][j] = 0;
				sx = j;
				sy = i;
			}
			else {
				field[i][j] = 0;
			}
		}
	}
	queue<aa>que;
	que.push(aa{ sx,sy,bitset<20>((1 << (num)) - 1),0 });
	map<aa,int>mp;
	mp[que.front()]=0;
	int ans = -1;
	while (!que.empty()) {
		aa atop(que.front());
		que.pop();
		if (atop.y >= 18) {
			ans = atop.turn;
			break;
		}
		for (int way = 0; way < 8; ++way) {
			bitset<20>bs(atop.bs);
			pair<int, int>p(amove(way, atop.x, atop.y, bs));
			if (p.first != -1) {
				if (mp.find(aa{ p.first,p.second,bs, 0 }) == mp.end()) {
					mp[aa{ p.first,p.second,bs,0 }] = atop.turn + 1;
					que.push(aa{ p.first,p.second,bs,atop.turn + 1 });
				}
			}
		}
	}
	cout << ans << endl;
	return 0;
}

