					pc[ny][nx] = '.';
					ny = ny + dy[i];
					nx = nx + dx[i];
				}
				if(ny == -1) continue;

				if(memo.count(pc)){
					auto& it = memo[pc];
					if(it[ny][nx] > cost + 1){
						it[ny][nx] = cost + 1;
						q.push(T(make_pair(ny,nx), pc, cost + 1));
					}
				}else{
					vector<vector<int>> dis(h, vector<int>(w,INF));
					dis[ny][nx] = cost + 1;
					memo[pc] = dis;
					q.push(T(make_pair(ny,nx), pc, cost + 1));
